import React, { useState } from 'react';
import bgImage from './fondocongreso.png'; 
import startupLogo from './startupiush.png';
import '../styles/HomePage.css';

const HomePage = ({ navigate }) => {
    const [startupLogoError, setStartupLogoError] = useState(false);

    const handleLoginClick = () => {
        navigate('login');
    };

    const handleHabeasdataClick = () => {
        navigate('habeasdata');
    };

    const handleStartupLogoError = () => {
        console.error("No se pudo cargar startupiush.png");
        setStartupLogoError(true);
    };

    return (
        <div>
            <div className="container" style={{backgroundImage: `url(${bgImage})`}}>
                <div className="blur-overlay" />
                <img className="logo" src="logo.png" alt="Logo" onError={() => console.error("No se pudo cargar logo.png")} />
                <div className="content">
                    {!startupLogoError ? (
                        <img 
                            className="startup-logo"
                            src={startupLogo}
                            alt="Logo Startup IUSH" 
                            onError={handleStartupLogoError}
                        />
                    ) : (
                        <p className="error-text">No se pudo cargar el logo de Startup IUSH</p>
                    )}
                    <p className="description">Congreso iberoamericano de emprendimiento</p>
                    <button className="button" onClick={handleHabeasdataClick}>Registrar emprendedor</button>
                    <button className="button login-button" onClick={handleLoginClick}>Login</button>
                </div>              
            </div>
        </div>
    );
};

export default HomePage;
