import React from 'react';
import styled from 'styled-components';
import videoFile from './iush.mp4'; // Ruta cot

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
`;

const VideoBackground = styled.video`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    z-index: -1; /* Asegura que el video esté detrás del contenido */
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4); /* rosado */
    backdrop-filter: blur(15px);
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const Logo = styled.img`
    width: 200px; /* Ajusta el ancho según sea necesario */
    height: auto; /* Mantén la proporción de la imagen */
    margin-bottom: 2px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center; /* Alinea al centro horizontalmente */
    margin-bottom: 20px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px; /* del tipo de borde 50px */
    border: none; /* Eliminado el borde */
    background: #213B6B; /* Cambiado el fondo */
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3); /* Añadido el box-shadow */
    color: white; /* Color del texto */
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px; /* Cambio el tamaño de fuente */
    font-weight: 400; /* Cambio el peso de la fuente */
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word; /*  controlar el comportamiento del texto  */
    margin: 10px; /* Añadido el margen */
`;

const HomePage = ({ navigate }) => {
    const handleLoginClick = () => {
        navigate('login');
    };

    const handleFormEmpClick = () => {
        navigate('registro_emp');
    };

    const handleFormEmprendedorClick = () => {
        navigate('registro_emprendedor');
    };

    const handleFormCrearClick = () => {
        navigate('crear_evaluador');
    };

    const handleAsignacionClick = () => {
        navigate('asignar_emprendimiento');
    };

    const handleInicioEmprednedorClick = () => {
        navigate('inicio_emprendedor');
    };

    const handleInicioAdminClick = () => {
        navigate('inicio_admin');
    };

    return (
        <div>
            <Container>
                <VideoBackground autoPlay muted loop>
                    <source src={videoFile} type="video/mp4" />
                    Your browser does not support the video tag.
                </VideoBackground>
                <BlurOverlay />
                <Content>
                    <Logo src="logo.png" alt="Logo" />
                    <Description>Congreso iberoamericano de emprendimiento</Description>
                    <Button onClick={handleLoginClick}>Login</Button>
                    <Button onClick={handleFormEmprendedorClick}>Emprendedor</Button>
                    {/* <Button onClick={handleFormEmpClick}>Registro</Button> */}
                    {/* <Button onClick={handleFormCrearClick}>Crear</Button> */}
                    {/* <Button onClick={handleAsignacionClick}>Asignar</Button> */}
                    {/* <Button onClick={handleInicioEmprednedorClick}>Inicio emprendedor</Button> */}
                    {/* <Button onClick={handleInicioAdminClick}>Admin</Button> */}
                </Content>
            </Container>
        </div>
    );
};

export default HomePage;
