import React from 'react';
import styled from 'styled-components';
import { useState , useEffect, useRef } from 'react';
import bgImage from './fondo_admin.jpg'; 
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { createGlobalStyle } from 'styled-components';

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(33, 59, 107, 0.4); /* azul */
    backdrop-filter: blur(15px);
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;
const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 5px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const FormContainer = styled.div`
    width: 90%;
    max-width: 600px;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;
    @media (max-width: 768px) {
        width: 95%;
    }
    @media (max-width: 480px) {
        width: 100%;
        padding: 10px;
    }
`;

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
`;


const Label = styled.label`
    font-family: 'Hint Vadodara', sans-serif;
    color: black;
    width: 100%;
    max-width: 400px;
    text-align: left;
`;

const Input = styled.input`
    width: 100%;
    max-width: 400px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
`;

const Select = styled.select`
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    width: 100%;
    max-width: 400px;
    position: relative;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;
    transition: background 0.3s;

    &:hover {
        background: #67157D;
    }
`;

const Option = styled.option`
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 20px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #67157D; /* Color de fondo */
    width: 50%;
`;

const DatePickerContainer = styled.div`
    width: 100%;
    max-width: 400px;
    margin-bottom: 10px;
`;

const StyledDatePicker = styled(DatePicker)`
    width: 100%;
    max-width: 400px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
    color: black;
    box-sizing: border-box;

    &:focus {
        outline: none;
        border-color: #213B6B;
    }

    // Asegurarse de que el input dentro del DatePicker tenga el ancho completo
    .react-datepicker__input-container input {
        width: 100%;
        height: 100%;
        padding: 0;
        border: none;
        background: transparent;
    }
`;

const GlobalStyle = createGlobalStyle`
    .react-datepicker-wrapper, .react-datepicker__input-container {
        display: block;
        width: 100%;
    }

    .react-datepicker {
        font-family: 'Hint Vadodara', sans-serif;
    }
    .react-datepicker__header {
        background-color: #213B6B;
    }
    .react-datepicker__current-month, .react-datepicker__day-name {
        color: white;
    }
    .react-datepicker__day--selected {
        background-color: #213B6B;
    }
`;


const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 10px;
`;

// Estilo para el párrafo de descripción del sector
const SectorDescription = styled.p`
    font-family: 'Hint Vadodara', sans-serif;
    color: #213B6B;
    font-size: 0.9rem;
    margin-top: 5px;
    text-align: left;
    width: 100%;
    max-width: 400px;
`;
// Estilo para el mensaje de carga
const LoadingMessage = styled.p`
    font-family: 'Hint Vadodara', sans-serif;
    color: #213B6B;
    font-size: 0.9rem;
`;

//ventana flotante
const ModalOverlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(33, 59, 107, 0.8);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1000;
`;

const ModalContent = styled.div`
    background: white;
    padding: 30px;
    border-radius: 10px;
    text-align: center;
    max-width: 400px;
    width: 90%;
    display: flex;
    flex-direction: column;
    align-items: center;
`;

const ModalLogo = styled.img`
    width: 150px;
    height: auto;
    margin-bottom: 20px;
`;

const ModalText = styled.p`
    font-size: 1.2rem;
    margin-bottom: 20px;
    color: #213B6B;
`;

const ModalButton = styled(Button)`
    margin-top: 20px;
`;



const FormPage = ({ navigate }) => {
    const handleInicioEmprednedorClick = () => {
        navigate('inicio_emprendedor');
    };

    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({});
    const [selectedDate, setSelectedDate] = useState(null);
    const [nacionalidades, setNacionalidades] = useState([]);
    const [sectores, setSectores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [descripcionSector, setDescripcionSector] = useState('');
    const [programasAcademicos, setProgramasAcademicos] = useState([]);

    useEffect(() => {
        // Usar la variable nac para las nacionalidades
        let nac = {
            "ok": true,
            "data": [
                {
                    "_id": "6673472cf182f9b41b2c1290",
                    "nombre": "COL",
                    "descripcion": "COLOMBIA"
                },
                {
                    "_id": "66736b2317fb868463e7e60c",
                    "nombre": "ABW",
                    "descripcion": "ARUBA"
                },
                {
                    "_id": "6673713717fb868463e7e615",
                    "nombre": "AND",
                    "descripcion": "ANDORRA"
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        // Usar los datos de 'cosa' para configurar las nacionalidades
        const fetchNacionalidades = async () => {
            try {
                const data = nac;
                setNacionalidades(data.data || []);
            } catch (error) {
                console.error('Error fetching nacionalidades:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchNacionalidades();
    }, []);

    useEffect(() => {
        // Usar la variable 'sector' en lugar de hacer una llamada a fetch
        let sector = {
            "ok": true,
            "data": [
                {
                    "_id": "6670ce4c7acec2e5e71a96d6",
                    "sector": "Tecnología",
                    "descripcion": "Dispositivos electrónicos, desarrollo de software, aplicaciones móviles, servicios de TI, ciberseguridad, y más"
                },
                {
                    "_id": "6670ce887acec2e5e71a96d8",
                    "sector": "Salud y Bienestar",
                    "descripcion": "Clínicas, telemedicina, productos de salud, fitness, y bienestar mental."
                },
                {
                    "_id": "6670ce9f7acec2e5e71a96da",
                    "sector": "Educación",
                    "descripcion": "Plataformas de e-learning, tutorías en línea, capacitación corporativa."
                },
                {
                    "_id": "6670ceb47acec2e5e71a96dc",
                    "sector": "Alimentos y Bebidas",
                    "descripcion": "Restaurantes, alimentos orgánicos, delivery de comida, bebidas artesanales."
                },
                {
                    "_id": "6670cf097acec2e5e71a96de",
                    "sector": "Energía y Medio Ambiente",
                    "descripcion": "Energías renovables, productos sostenibles, reciclaje, gestión de residuos."
                },
                {
                    "_id": "6670cf267acec2e5e71a96e0",
                    "sector": "Fintech",
                    "descripcion": "Servicios financieros digitales, pagos en línea, criptomonedas, asesoría financiera."
                },
                {
                    "_id": "6670cf3e7acec2e5e71a96e2",
                    "sector": "Turismo y Hospitalidad",
                    "descripcion": "Agencias de viajes, alojamiento, experiencias turísticas, guías locales."
                },
                {
                    "_id": "6670cf697acec2e5e71a96e4",
                    "sector": "Construcción e Inmobiliaria",
                    "descripcion": "Desarrollo inmobiliario, servicios de construcción, bienes raíces, decoración de interiores."
                },
                {
                    "_id": "6670cf927acec2e5e71a96e6",
                    "sector": "Logística y Transporte",
                    "descripcion": "Servicios de transporte, logística, almacenamiento, entrega de última milla."
                },
                {
                    "_id": "6670d0007acec2e5e71a96e8",
                    "sector": "Medios y Entretenimiento",
                    "descripcion": "Producción audiovisual, videojuegos, eventos, contenido digital"
                },
                {
                    "_id": "6670d0247acec2e5e71a96ea",
                    "sector": "Agricultura y Agroindustria",
                    "descripcion": "Cultivos, ganadería, tecnología agrícola, productos procesados."
                },
                {
                    "_id": "6670d0577acec2e5e71a96ec",
                    "sector": "Retail y Comercio Electrónico",
                    "descripcion": "Tiendas en línea, marketplaces, dropshipping, venta al por menor."
                },
                {
                    "_id": "6670d0727acec2e5e71a96ee",
                    "sector": "Servicios Profesionales",
                    "descripcion": "Consultoría, marketing, recursos humanos, servicios legales."
                },
                {
                    "_id": "6670d08b7acec2e5e71a96f0",
                    "sector": "Bienes de Consumo",
                    "descripcion": "Productos de consumo masivo, electrodomésticos, artículos para el hogar."
                },
                {
                    "_id": "6670d4067acec2e5e71a96f2",
                    "sector": "Moda y Belleza",
                    "descripcion": "Ropa, accesorios, productos de belleza, cuidado personal, servicios de estilismo."
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        // Usar los datos de 'sector' para configurar los sectores
        const fetchSectores = async () => {
            try {
                const data = sector;
                setSectores(data.data || []);
            } catch (error) {
                console.error('Error fetching sectores:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchSectores();
    }, []);

    useEffect(() => {
        // Simular carga de programas académicos
        let programas = {
            "ok": true,
            "data": [
                {
                    "_id": "prog001",
                    "nombre": "Ingeniería de Sistemas",
                    "descripcion": "Programa de ingeniería enfocado en sistemas computacionales"
                },
                {
                    "_id": "prog002",
                    "nombre": "Administración de Empresas",
                    "descripcion": "Programa enfocado en la gestión y dirección de empresas"
                },
                {
                    "_id": "prog003",
                    "nombre": "Psicología",
                    "descripcion": "Programa enfocado en el estudio de la mente y el comportamiento humano"
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        const fetchProgramasAcademicos = async () => {
            try {
                const data = programas;
                setProgramasAcademicos(data.data || []);
            } catch (error) {
                console.error('Error fetching programas académicos:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchProgramasAcademicos();
    }, []);


    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: value,
        }));

        // Actualizar la descripción del sector basado en el sector seleccionado
        if (id === 'sectorIndustria') {
            const selectedSector = sectores.find(sector => sector.sector === value);
            setDescripcionSector(selectedSector ? selectedSector.descripcion : '');
        }
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
        setFormData((prevData) => ({
            ...prevData,
            fecha: date,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();


        const formattedData = {
            nombreEmprendimiento: formData.nombreEmprendedor || '',
            idSector: formData.sectorIndustria || '',
            idEmprendedor: formData.documentoIdentidad || '',
            fecha: formData.fecha ? formData.fecha.toISOString().split('T')[0] : '',
            respuestas: [
                { numeroPregunta: 1, respuestaPregunta: formData.ideaClara === 'si' },
                { numeroPregunta: 2, respuestaPregunta: formData.investigacionesMercado === 'si' },
                { numeroPregunta: 3, respuestaPregunta: formData.necesidadProblema === 'si' },
                { numeroPregunta: 4, respuestaPregunta: formData.retroalimentacionClientes === 'si' },
                { numeroPregunta: 5, respuestaPregunta: formData.propuestaValor === 'si' },
                { numeroPregunta: 6, respuestaPregunta: formData.planNegocios === 'si' },
                { numeroPregunta: 7, respuestaPregunta: formData.productosServicios === 'si' },
                { numeroPregunta: 8, respuestaPregunta: formData.prototipoPrueba === 'si' },
                { numeroPregunta: 9, respuestaPregunta: formData.competenciaAnalisis === 'si' },
                { numeroPregunta: 10, respuestaPregunta: formData.publicoObjetivo === 'si' },
                { numeroPregunta: 11, respuestaPregunta: formData.planFinanciero === 'si' },
                { numeroPregunta: 12, respuestaPregunta: formData.enElMercado === 'si' },
                { numeroPregunta: 13, respuestaPregunta: formData.campañaMarketing === 'si' },
                { numeroPregunta: 14, respuestaPregunta: formData.clientesUsuarios === 'si' },
                { numeroPregunta: 15, respuestaPregunta: formData.canalDistribucion === 'si' },
                { numeroPregunta: 16, respuestaPregunta: formData.presenciaEnLinea === 'si' },
                { numeroPregunta: 17, respuestaPregunta: formData.estrategiasPrecios === 'si' },
                { numeroPregunta: 18, respuestaPregunta: formData.ingresosConstantes === 'si' },
                { numeroPregunta: 19, respuestaPregunta: formData.reinversionCrecimiento === 'si' },
                { numeroPregunta: 20, respuestaPregunta: formData.contratandoEmpleados === 'si' },
                { numeroPregunta: 21, respuestaPregunta: formData.ampliadoGama === 'si' },
                { numeroPregunta: 22, respuestaPregunta: formData.metricasKPI === 'si' },
                { numeroPregunta: 23, respuestaPregunta: formData.sistemasGestion === 'si' },
                { numeroPregunta: 24, respuestaPregunta: formData.baseClientes === 'si' },
                { numeroPregunta: 25, respuestaPregunta: formData.puntoEquilibrio === 'si' },
                { numeroPregunta: 26, respuestaPregunta: formData.fidelizacionClientes === 'si' },
                { numeroPregunta: 27, respuestaPregunta: formData.procesosEstandarizados === 'si' },
                { numeroPregunta: 28, respuestaPregunta: formData.analisisRendimiento === 'si' },
                { numeroPregunta: 29, respuestaPregunta: formData.costosControlados === 'si' },
                { numeroPregunta: 30, respuestaPregunta: formData.satisfaccionCliente === 'si' },
                { numeroPregunta: 31, respuestaPregunta: formData.equipoMotivado === 'si' },
                { numeroPregunta: 32, respuestaPregunta: formData.beneficiosConstantes === 'si' },
                { numeroPregunta: 33, respuestaPregunta: formData.mejorasContinuas === 'si' },
                { numeroPregunta: 34, respuestaPregunta: formData.expansionMercados === 'si' },
                { numeroPregunta: 35, respuestaPregunta: formData.sociosEstrategicos === 'si' },
                { numeroPregunta: 36, respuestaPregunta: formData.financiamientoExterno === 'si' },
                { numeroPregunta: 37, respuestaPregunta: formData.planExpansion === 'si' },
                { numeroPregunta: 38, respuestaPregunta: formData.patentesMarcas === 'si' },
                { numeroPregunta: 39, respuestaPregunta: formData.estrategiaSalida === 'si' },
            ],
    };


    console.log('Datos del formulario enviados:', formattedData);
    setShowModal(true);
};


    return (
        <>
        <GlobalStyle />
        <Container>
            <BlurOverlay />
            <Content>
                <Logo src="logo_blanco.png" alt="Logo" />
                <Description>Formulario de registro de emprendimientos</Description>

                <FormContainer>
                    <Form onSubmit={handleSubmit}>

                <Label>Fecha de creación del emprendimiento</Label>
                                <DatePickerContainer>
                                    <StyledDatePicker id="fecha" selected={selectedDate} onChange={handleDateChange} dateFormat="yyyy-MM-dd" placeholderText="Selecciona una fecha" showMonthDropdown showYearDropdown dropdownMode="select" />
                                </DatePickerContainer>

                    <Label>Sector o industria del emprendimiento</Label>
                            {loading ? (
                                <LoadingMessage>Cargando sectores...</LoadingMessage>
                            ) : (
                                <Select id="sectorIndustria" value={formData.sectorIndustria || ''} onChange={handleChange} required>
                                    <Option value="">Seleccionar</Option>
                                    {sectores.map((sec) => (
                                        <Option key={sec._id} value={sec.sector}>{sec.sector}</Option>
                                    ))}
                                </Select>
                            )}
                            {descripcionSector && (
                                <SectorDescription>Descripción del sector: {descripcionSector}</SectorDescription>
                            )}


                <Label>¿Tienes una idea clara y definida de tu emprendimiento?</Label>
                <Select id="ideaClara" value={formData.ideaClara || ''} onChange={handleChange} required>
                    <Option value="">Seleccionar</Option>
                    <Option value="si">Sí</Option>
                    <Option value="no">No</Option>
                </Select>

                <Label>¿Has realizado investigaciones de mercado para validar tu idea?</Label>
                <Select id="investigacionesMercado" value={formData.investigacionesMercado || ''} onChange={handleChange} required>
                    <Option value="">Seleccionar</Option>
                    <Option value="si">Sí</Option>
                    <Option value="no">No</Option>
                </Select>

                <Label>¿Has identificado una necesidad o problema específico que tu idea busca resolver?</Label>
                <Select id="necesidadProblema" value={formData.necesidadProblema || ''} onChange={handleChange} required>
                    <Option value="">Seleccionar</Option>
                    <Option value="si">Sí</Option>
                    <Option value="no">No</Option>
                </Select>

                <Label>¿Has recibido retroalimentación de posibles clientes sobre tu idea?</Label>
                <Select id="retroalimentacionClientes" value={formData.retroalimentacionClientes || ''} onChange={handleChange} required>
                    <Option value="">Seleccionar</Option>
                    <Option value="si">Sí</Option>
                    <Option value="no">No</Option>
                </Select>

                <Label>¿Tienes una propuesta de valor única para tu producto/servicio?</Label>
                <Select id="propuestaValor" value={formData.propuestaValor || ''} onChange={handleChange} required>
                    <Option value="">Seleccionar</Option>
                    <Option value="si">Sí</Option>
                    <Option value="no">No</Option>
                </Select>

                <Label>¿Tienes un plan de negocios formal y detallado?</Label>
                <Select id="planNegocios" value={formData.planNegocios || ''} onChange={handleChange} required>
                    <Option value="">Seleccionar</Option>
                    <Option value="si">Sí</Option>
                    <Option value="no">No</Option>
                </Select>

                <Label>¿Has definido tus productos o servicios específicos?</Label>
                <Select id="productosServicios" value={formData.productosServicios || ''} onChange={handleChange} required>
                    <Option value="">Seleccionar</Option>
                    <Option value="si">Sí</Option>
                    <Option value="no">No</Option>
