import React from 'react';
import styled from 'styled-components';
import bgImage from './fondo_admin.jpg'; // Ruta correcta de tu imagen

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4); /* rosado */
    backdrop-filter: blur(15px);
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 90%; /* 90% del BlurOverlay */
    margin: 0 auto;
    height: 90%;
`;

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 5px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const FormContainer = styled.div`
    width: 100%;
    max-width: 600px;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;
`;

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
`;

const Label = styled.label`
    font-family: 'Hint Vadodara', sans-serif;
    color: black;
    width: 100%;
    text-align: left;
`;

const Input = styled.input`
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
`;

const Select = styled.select`
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    width: 100%;
    position: relative;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;
    transition: background 0.3s;

    &:hover {
        background: #67157D;
    }
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 10px;
    margin-top: 20px;
`;

const InicioEmprendedor = ({ navigate }) => {
    const handleInicioClick = () => {
        navigate('home');
    };

    const handleFormCrearClick = () => {
        navigate('crear_evaluador');
    };

    const handleAsignacionClick = () => {
        navigate('asignar_emprendimiento');
    };

    const handleSubmit = (e) => {
        e.preventDefault();
    };

    return (
        <div>
            <Container>
                <BlurOverlay>
                    <Content>
                        <Logo src="logo_blanco.png" alt="Logo" />
                        <Description>Bienvenido administrador a la gestion de evaluadores</Description>

                        <FormContainer>
                            <Form onSubmit={handleSubmit}>
                                {/* Aquí irían los campos del formulario */}
                            </Form>
                        </FormContainer>

                        <ButtonContainer>
                            <Button onClick={handleFormCrearClick}>Registrar</Button>
                            <Button onClick={handleAsignacionClick}>Asignación</Button>
                            <Button onClick={handleInicioClick}>Cancelar</Button>
                        </ButtonContainer>
                    </Content>
                </BlurOverlay>
            </Container>
        </div>
    );
};

export default InicioEmprendedor;
