import React, { useState } from 'react';
import styled from 'styled-components';
import bgImage from './fondo_admin.jpg';

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4);
    backdrop-filter: blur(15px);
    display: flex;
    justify-content: space-between;
`;

const Content = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
`;

const SidePanel = styled.div`
    width: 250px;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    border-radius: 10px 0 0 10px;
    box-shadow: -5px 0 10px rgba(0, 0, 0, 0.1);
    height: 100%;
`;

const Logo = styled.img`
    width: 150px;
    height: auto;
    margin-bottom: 20px;
`;

const MenuOption = styled.button`
    width: 100%;
    padding: 10px;
    margin: 5px 0;
    background: ${props => props.disabled ? '#ccc' : '#213B6B'};
    color: white;
    border: none;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
    opacity: ${props => props.disabled ? 0.6 : 1};

    &:hover {
        background: ${props => props.disabled ? '#ccc' : '#67157D'};
    }
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const FormContainer = styled.div`
    width: 90%;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;
    max-width: 90%;
    display: ${props => props.visible ? 'block' : 'none'};
`;

const InicioEmprendedor = ({ navigate }) => {
    const [formVisible, setFormVisible] = useState(false);

    const handleMenuClick = (route) => {
        setFormVisible(true);
        navigate(route);
    };

    return (
        <Container>
            <BlurOverlay>
                <Content>
                    <Description>Bienvenido administrador a la gestión de evaluadores y emprendimientos</Description>
                    <FormContainer visible={formVisible}>
                        {/* Aquí se puede agregar el contenido específico para cada opción del menú */}
                    </FormContainer>
                </Content>
                <SidePanel>
                    <Logo src="logo_congreso.png" alt="Logo del Congreso" />
                    <MenuOption onClick={() => handleMenuClick('listar_evaluadores')}>Listar evaluadores</MenuOption>
                    <MenuOption onClick={() => handleMenuClick('crear_evaluador')}>Crear evaluadores</MenuOption>
                    <MenuOption onClick={() => handleMenuClick('asignar_emprendimiento')}>Asignar evaluador</MenuOption>
                    <MenuOption onClick={() => handleMenuClick('listar_emprendimientos')}>Listar emprendimientos</MenuOption>
                    <MenuOption disabled>Informes</MenuOption>
                </SidePanel>
            </BlurOverlay>
        </Container>
    );
};

export default InicioEmprendedor;
