import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const TableContainer = styled.div`
    max-width: 1000px;
    margin: 0 auto;
    padding: 20px;
`;

const Table = styled.table`
    width: 100%;
    border-collapse: separate;
    border-spacing: 0;
    border: 1px solid #213B6B;
    border-radius: 8px;
    overflow: hidden;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 14px;
`;

const Th = styled.th`
    background-color: #213B6B;
    color: white;
    padding: 12px 15px;
    text-align: left;
`;

const Td = styled.td`
    background-color: rgba(33, 59, 107, 0.05);
    color: #333;
    padding: 12px 15px;
    border-bottom: 1px solid #ccc;
`;

const Tr = styled.tr`
    &:last-child td {
        border-bottom: none;
    }
`;

const ListarEmprendimientos = () => {
    const [emprendimientos, setEmprendimientos] = useState([]);

    useEffect(() => {
        // Simulando la obtención de datos del servidor
        const fetchData = () => {
            const result = {
                "ok": true,
                "data": [
                    {
                        "fecha": "2021/01/01",
                        "estado": "Idea",
                        "sector": {
                            "sector": "Tecnología",
                        },
                        "infoEmprendedor": {
                            "nombres": "persona1",
                            "apellidos": "apellido",
                        },
                        "IES": {
                            "nombre": "UNIVERSIDAD D'ANDORRA",
                        },
                        "pais": {
                            "descripcion": "ANDORRA",
                        }
                    },
                    // ... más datos aquí ...
                ],
                "message": "Todo ok",
                "error": null
            };

            if (result.ok) {
                setEmprendimientos(result.data);
            } else {
                console.error('Error fetching data:', result.message);
            }
        };

        fetchData();
    }, []);

    return (
        <TableContainer>
            <Table>
                <thead>
                    <tr>
                        <Th>Estado</Th>
                        <Th>Sector</Th>
                        <Th>Emprendedor</Th>
                        <Th>IES</Th>
                        <Th>País</Th>
                    </tr>
                </thead>
                <tbody>
                    {emprendimientos.map((emprendimiento, index) => (
                        <Tr key={index}>
                            <Td>{emprendimiento.estado}</Td>
                            <Td>{emprendimiento.sector.sector}</Td>
                            <Td>{`${emprendimiento.infoEmprendedor.nombres} ${emprendimiento.infoEmprendedor.apellidos}`}</Td>
                            <Td>{emprendimiento.IES.nombre}</Td>
                            <Td>{emprendimiento.pais.descripcion}</Td>
                        </Tr>
                    ))}
                </tbody>
            </Table>
        </TableContainer>
    );
};

export default ListarEmprendimientos;
