import React, { useState } from 'react';
import styled from 'styled-components';
import bgImage from './fondo_login.jpg';
import pdfFile from './hd.pdf';

// ... (otros styled components permanecen iguales)

const FormContainer = styled.div`
    width: 90%;
    max-width: 600px;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    position: relative;
    @media (max-width: 768px) {
        width: 95%;
    }
    @media (max-width: 480px) {
        width: 100%;
        padding: 10px;
    }
`;

const PDFViewer = styled.iframe`
    width: 100%;
    height: 100%;
    border: none;
    position: absolute;
    top: -40px;  // Ajusta este valor según sea necesario
    left: 0;
`;

const PDFOverlay = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 40px;  // Ajusta este valor para que coincida con el top del PDFViewer
    background-color: white;
    z-index: 1;
`;

// ... (otros styled components permanecen iguales)

const HabeasData = ({ navigate }) => {
    const [accepted, setAccepted] = useState(false);

    const handleCheckboxChange = (e) => {
        setAccepted(e.target.checked);
    };

    const handleNext = () => {
        if (accepted) {
            navigate('registro_emprendedor');
        }
    };

    return (
        <Container>
            <BlurOverlay />
            <Content>
                <Logo src="logo.png" alt="Logo" />
                <Description>Habeas Data y Términos y Condiciones</Description>
                <FormContainer>
                    <PDFOverlay />
                    <PDFViewer src={pdfFile} title="Habeas Data PDF" />
                </FormContainer>
                <CheckboxContainer>
                    <Checkbox 
                        type="checkbox" 
                        id="acceptTerms" 
                        checked={accepted} 
                        onChange={handleCheckboxChange}
                    />
                    <Label htmlFor="acceptTerms">Acepto los términos y condiciones</Label>
                </CheckboxContainer>
                <Button onClick={handleNext} disabled={!accepted}>
                    Siguiente
                </Button>
            </Content>
        </Container>
    );
};

export default HabeasData;
