import React, { useState } from 'react';
import styled from 'styled-components';
import bgImage from './fondo_admin.jpg'; 
import FormCrearEvaluador from './FormCrearEvaluador';
import AsignacionEmprendimientos from './AsignacionEmprendimiento';

const Container = styled.div`
    display: flex;
    min-height: 100vh;
    width: 100%;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
    overflow: hidden;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4);
    backdrop-filter: blur(15px);
    display: flex;
`;

const Content = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    margin-left: 290px;
`;

const Logo = styled.img`
    width: 150px;
    height: auto;
    margin-bottom: 20px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const FormContainer = styled.div`
    width: 90%;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;
    max-width: 90%;
    display: ${props => props.visible ? 'block' : 'none'};
`;

const SidePanel = styled.div`
    width: 250px;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    border-radius: 20px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    max-height: calc(100vh - 30px);
    overflow-y: auto;
    position: fixed;
    left: 10px;
    top: 15px;
    bottom: 15px;
`;

const MenuOption = styled.button`
    width: 100%;
    padding: 10px;
    margin: 5px 0;
    background: ${props => props.disabled ? '#ccc' : '#213B6B'};
    color: white;
    border: none;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
    opacity: ${props => props.disabled ? 0.6 : 1};

    &:hover {
        background: ${props => props.disabled ? '#ccc' : '#67157D'};
    }
`;
const InicioAdmin = () => {
    const [formVisible, setFormVisible] = useState(false);
    const [activeForm, setActiveForm] = useState(null);

    const {
        evaluadores,
        emprendimientos,
        selectedEvaluador,
        selectedEmprendimientos,
        assignments,
        disabledEvaluadores,
        disabledEmprendimientos,
        handleEvaluadorClick,
        handleEmprendimientoClick,
        handleAsignar,
        handleLimpiar,
        handleDeleteAssignment
    } = AsignacionEmprendimientos();

    const handleMenuClick = (form) => {
        setFormVisible(true);
        setActiveForm(form);
    };

    const handleRegresarClick = () => {
        setFormVisible(false);
        setActiveForm(null);
    };

    return (
        <Container>
            <BlurOverlay>
                <SidePanel>
                    <Logo src="logo.png" alt="Logo del Congreso" />
                    <MenuOption onClick={() => handleMenuClick('listar_evaluadores')}>Listar evaluadores</MenuOption>
                    <MenuOption onClick={() => handleMenuClick('crear_evaluador')}>Crear evaluadores</MenuOption>
                    <MenuOption onClick={() => handleMenuClick('asignar_emprendimiento')}>Asignar evaluador</MenuOption>
                    <MenuOption onClick={() => handleMenuClick('listar_emprendimientos')}>Listar emprendimientos</MenuOption>
                    <MenuOption disabled>Informes</MenuOption>
                </SidePanel>
                <Content>
                    <Logo src="logo.png" alt="Logo del Congreso" />
                    <Description>Bienvenido administrador a la gestión de evaluadores</Description>
                    <FormContainer visible={formVisible}>
                        {activeForm === 'crear_evaluador' && (
                            <FormCrearEvaluador handleRegresar={handleRegresarClick} />
                        )}
                        {activeForm === 'asignar_emprendimiento' && (
                            <div>
                                <h2>Asignación de Emprendimientos</h2>
                                <div>
                                    <h3>Evaluadores</h3>
                                    {evaluadores.map(evaluador => (
                                        <button
                                            key={evaluador.id}
                                            onClick={() => handleEvaluadorClick(evaluador)}
                                            disabled={disabledEvaluadores.includes(evaluador.id)}
                                        >
                                            {evaluador.nombre}
                                        </button>
                                    ))}
                                </div>
                                <div>
                                    <h3>Emprendimientos</h3>
                                    {emprendimientos.map(emprendimiento => (
                                        <button
                                            key={emprendimiento.id}
                                            onClick={() => handleEmprendimientoClick(emprendimiento.id)}
                                            disabled={disabledEmprendimientos.includes(emprendimiento.id)}
                                        >
                                            {emprendimiento.nombre}
                                        </button>
                                    ))}
                                </div>
                                <button onClick={handleAsignar}>Asignar</button>
                                <button onClick={handleLimpiar}>Limpiar Todo</button>
                                {assignments.length > 0 && (
                                    <table>
                                        <thead>
                                            <tr>
                                                <th>Evaluador</th>
                                                <th>Emprendimientos</th>
                                                <th>Acción</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {assignments.map((assignment, index) => (
                                                <tr key={index}>
                                                    <td>{assignment.evaluador}</td>
                                                    <td>{assignment.emprendimientos.map(e => e.nombre).join(', ')}</td>
                                                    <td>
                                                        <button onClick={() => handleDeleteAssignment(index, assignment.evaluadorId, assignment.emprendimientos)}>
                                                            Eliminar
                                                        </button>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                )}
                            </div>
                        )}
                    </FormContainer>
                </Content>
            </BlurOverlay>
        </Container>
    );
};

export default InicioAdmin;
