import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 15px;
    width: 100%;
    max-width: 500px;
    margin: 0 auto;
`;

const Label = styled.label`
    font-family: 'Hint Vadodara', sans-serif;
    color: black;
    width: 100%;
    text-align: left;
`;

const Input = styled.input`
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
`;

const SelectContainer = styled.div`
    width: 100%;
    position: relative;
`;

const SearchInput = styled.input`
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
    box-sizing: border-box;
    cursor: pointer;
`;

const OptionsList = styled.div`
    position: absolute;
    width: 100%;
    max-height: 200px;
    overflow-y: auto;
    background-color: white;
    border: 1px solid #ccc;
    border-radius: 5px;
    z-index: 10;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
`;

const OptionItem = styled.div`
    padding: 10px;
    cursor: pointer;
    color: black;
    &:hover {
        background-color: #67157D;
        color: white;
    }
`;

const ClearButton = styled.span`
    position: absolute;
    top: 50%;
    right: 10px;
    transform: translateY(-50%);
    cursor: pointer;
    color: #666;
    font-size: 18px;
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 10px;
    width: 100%;
`;

const Button = styled.button`
    padding: 10px 20px;
    border-radius: 5px;
    border: none;
    background: #213B6B;
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 16px;
    cursor: pointer;
    transition: background 0.3s;

    &:hover {
        background: #67157D;
    }
`;

const FormCrearEvaluador = ({ handleRegresar }) => {
    const [selectedInstitutionId, setSelectedInstitutionId] = useState('');
    const [selectedCountryId, setSelectedCountryId] = useState('');
    const [formData, setFormData] = useState({
        nombre: '',
        institucion: '',
        telefono: '',
        correo: '',
        pais: ''
    });

    const [searchTermInstitucion, setSearchTermInstitucion] = useState('');
    const [searchTermPais, setSearchTermPais] = useState('');
    const [filteredInstituciones, setFilteredInstituciones] = useState([]);
    const [filteredPaises, setFilteredPaises] = useState([]);
    const [instituciones, setInstituciones] = useState([]);
    const [paises, setPaises] = useState([]);
    const [isInstitucionOptionsOpen, setIsInstitucionOptionsOpen] = useState(false);
    const [isPaisOptionsOpen, setIsPaisOptionsOpen] = useState(false);

    const institucionOptionsListRef = useRef(null);
    const paisOptionsListRef = useRef(null);

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const evaluadorData = [{
            nombre: formData.nombre,
            idIES: selectedInstitutionId,
            telefono: formData.telefono,
            correo: formData.correo,
            idPais: selectedCountryId
        }];
        console.log(evaluadorData);

        //servicio de envio
        /*
        try {
            const response = await axios.post('/api/evaluadores', evaluadorData);
            console.log('Evaluador creado:', response.data);
            // adminstrar la logica aqui
        } catch (error) {
            console.error('Error creando evaluador:', error);
            // adicionar error aqui
        }
        */
    };

    const handleSearchChangeInstitucion = (e) => {
        const value = e.target.value;
        setSearchTermInstitucion(value);
        setFilteredInstituciones(
            instituciones.filter(ies =>
                ies.nombre.toLowerCase().includes(value.toLowerCase())
            )
        );
    };

    const handleSearchChangePais = (e) => {
        const value = e.target.value;
        setSearchTermPais(value);
        setFilteredPaises(
            paises.filter(pais =>
                pais.descripcion.toLowerCase().includes(value.toLowerCase())
            )
        );
    };

    const handleOptionClickInstitucion = (nombre, id) => {
        setFormData(prevData => ({
            ...prevData,
            institucion: nombre
        }));
        setSelectedInstitutionId(id);
        setSearchTermInstitucion(nombre);
        setIsInstitucionOptionsOpen(false);
    };

    const handleOptionClickPais = (descripcion, id) => {
        setFormData(prevData => ({
            ...prevData,
            pais: descripcion
        }));
        setSelectedCountryId(id);
        setSearchTermPais(descripcion);
        setIsPaisOptionsOpen(false);
    };

    const toggleOptionsInstitucion = () => {
        setIsInstitucionOptionsOpen(!isInstitucionOptionsOpen);
    };

    const toggleOptionsPais = () => {
        setIsPaisOptionsOpen(!isPaisOptionsOpen);
    };

    const clearInstitucion = () => {
        setFormData(prevData => ({
            ...prevData,
            institucion: ''
        }));
        setSearchTermInstitucion('');
        setFilteredInstituciones(instituciones);
    };

    const clearPais = () => {
        setFormData(prevData => ({
            ...prevData,
            pais: ''
        }));
        setSearchTermPais('');
        setFilteredPaises(paises);
    };

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (
                institucionOptionsListRef.current &&
                !institucionOptionsListRef.current.contains(event.target) &&
                !event.target.closest('#searchInputInstitucion')
            ) {
                setIsInstitucionOptionsOpen(false);
            }
            if (
                paisOptionsListRef.current &&
                !paisOptionsListRef.current.contains(event.target) &&
                !event.target.closest('#searchInputPais')
            ) {
                setIsPaisOptionsOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    useEffect(() => {
        // datos del servicio
        /*
        const fetchData = async () => {
            try {
                const [paisesResponse, iesResponse] = await Promise.all([
                    axios.get('/api/paises'),
                    axios.get('/api/instituciones')
                ]);

                setPaises(paisesResponse.data || []);
                setFilteredPaises(paisesResponse.data || []);
                setInstituciones(iesResponse.data || []);
                setFilteredInstituciones(iesResponse.data || []);
            } catch (error) {
                console.error('Error fetching data:', error);
                // logica del error
            }
        };

        fetchData();
        */

        // datos de prueba locales

        let paisesData = {
            "ok": true,
            "data": [
                {
                    "_id": "6673472cf182f9b41b2c1290",
                    "nombre": "COL",
                    "descripcion": "COLOMBIA"
                },
                {
                    "_id": "66736b2317fb868463e7e60c",
                    "nombre": "ABW",
                    "descripcion": "ARUBA"
                },
                {
                    "_id": "6673713717fb868463e7e615",
                    "nombre": "AND",
                    "descripcion": "ANDORRA"
                }
