import React from 'react';
import styled from 'styled-components';
import { useState, useEffect, useRef } from 'react';
import bgImage from './fondo_login.jpg';

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4);
    backdrop-filter: blur(15px);
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const FormContainer = styled.div`
    width: 90%;
    max-width: 600px;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;
    @media (max-width: 768px) {
        width: 95%;
    }
    @media (max-width: 480px) {
        width: 100%;
        padding: 10px;
    }
`;

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
`;

const Label = styled.label`
    font-family: 'Hint Vadodara', sans-serif;
    color: black;
    width: 100%;
    max-width: 400px;
    text-align: left;
`;

const Input = styled.input`
    width: 100%;
    max-width: 400px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
`;

const SelectContainer = styled.div`
    width: 100%;
    max-width: 400px;
    position: relative;
`;

const SearchInput = styled.input`
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
    box-sizing: border-box;
    cursor: pointer;
`;

const OptionsList = styled.div`
    position: absolute;
    width: 100%;
    max-height: 200px;
    overflow-y: auto;
    background-color: white;
    border: 1px solid #ccc;
    border-radius: 5px;
    z-index: 10;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
`;

const OptionItem = styled.div`
    padding: 10px;
    cursor: pointer;
    color: black;
    &:hover {
        background-color: #67157D;
        color: white;
    }
`;

const ClearButton = styled.span`
    position: absolute;
    top: 50%;
    right: 10px;
    transform: translateY(-50%);
    cursor: pointer;
    color: #666;
    font-size: 18px;
`;

const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 5px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 10px;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;
    transition: background 0.3s;

    &:hover {
        background: #67157D;
    }
`;

const FormEmprendedor = ({ navigate }) => {
    const [formData, setFormData] = useState({
        nombreEmprendedor: '',
        nacionalidad: '',  
        ciudadResidencia: '',
        documentoIdentidad: '',
        correoPersonal: '',
        correoInstitucional: '',
        telefono: '',
        nombreInstitucion: '',
        programaAcademico: ''
    });

    const [searchTermNacionalidad, setSearchTermNacionalidad] = useState('');
    const [searchTermInstitucion, setSearchTermInstitucion] = useState('');
    const [filteredNacionalidades, setFilteredNacionalidades] = useState([]);
    const [filteredInstituciones, setFilteredInstituciones] = useState([]);
    const [loading, setLoading] = useState(true);
    const [nacionalidades, setNacionalidades] = useState([]);
    const [instituciones, setInstituciones] = useState([]);
    const [isNacionalidadOptionsOpen, setIsNacionalidadOptionsOpen] = useState(false);
    const [isInstitucionOptionsOpen, setIsInstitucionOptionsOpen] = useState(false);

    const nacionalidadOptionsListRef = useRef(null);
    const institucionOptionsListRef = useRef(null);

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
    };

    const handleBack = () => {
        navigate('home');
    };

    const handleSearchChangeNacionalidad = (e) => {
        const value = e.target.value;
        setSearchTermNacionalidad(value);
        setFilteredNacionalidades(
            nacionalidades.filter(nac =>
                nac.descripcion.toLowerCase().includes(value.toLowerCase())
            ).slice(0, 4) // Limitar a 4 resultados
        );
    };

    const handleSearchChangeInstitucion = (e) => {
        const value = e.target.value;
        setSearchTermInstitucion(value);
        setFilteredInstituciones(
            instituciones.filter(ies =>
                ies.nombre.toLowerCase().includes(value.toLowerCase())
            ).slice(0, 4) // Limitar a 4 resultados
        );
    };

    const handleOptionClickNacionalidad = (descripcion) => {
        setFormData(prevData => ({
            ...prevData,
            nacionalidad: descripcion
        }));
        setSearchTermNacionalidad(descripcion); // Actualiza el campo con la descripción de la nacionalidad seleccionada
        setIsNacionalidadOptionsOpen(false);
    };

    const handleOptionClickInstitucion = (nombre) => {
        setFormData(prevData => ({
            ...prevData,
            nombreInstitucion: nombre
        }));
        setSearchTermInstitucion(nombre); // Actualiza el campo con el nombre de la institución seleccionada
        setIsInstitucionOptionsOpen(false);
    };

    const toggleOptionsNacionalidad = () => {
        setIsNacionalidadOptionsOpen(!isNacionalidadOptionsOpen);
    };

    const toggleOptionsInstitucion = () => {
        setIsInstitucionOptionsOpen(!isInstitucionOptionsOpen);
    };

    const clearNacionalidad = () => {
        setFormData(prevData => ({
            ...prevData,
            nacionalidad: ''
        }));
        setSearchTermNacionalidad(''); // Limpia el campo de búsqueda de nacionalidad
        setFilteredNacionalidades(nacionalidades);
    };

    const clearInstitucion = () => {
        setFormData(prevData => ({
            ...prevData,
            nombreInstitucion: ''
        }));
        setSearchTermInstitucion(''); // Limpia el campo de búsqueda de institución
        setFilteredInstituciones(instituciones);
    };

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (
                nacionalidadOptionsListRef.current &&
                !nacionalidadOptionsListRef.current.contains(event.target) &&
                !event.target.closest('#searchInputNacionalidad')
            ) {
                setIsNacionalidadOptionsOpen(false);
            }
            if (
                institucionOptionsListRef.current &&
                !institucionOptionsListRef.current.contains(event.target) &&
                !event.target.closest('#searchInputInstitucion')
            ) {
                setIsInstitucionOptionsOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    useEffect(() => {
        // Simulación de la respuesta del servidor
        const nac = {
            "ok": true,
            "data": [
                { "_id": "6673472cf182f9b41b2c1290", "nombre": "COL", "descripcion": "COLOMBIA" },
                { "_id": "66736b2317fb868463e7e60c", "nombre": "ABW", "descripcion": "ARUBA" },
                { "_id": "6673713717fb868463e7e615", "nombre": "AND", "descripcion": "ANDORRA" }
            ],
            "message": "Todo ok",
            "error": null
        };

        const ies = {
            "ok": true,
            "data": [
                { "_id": "66736b9b17fb868463e7e60e", "nombre": "Xavier University School of Medicine, Aruba", "paisIES": "66736b2317fb868463e7e60c" },
                { "_id": "66736fd217fb868463e7e611", "nombre": "University of Aruba", "paisIES": "66736b2317fb868463e7e60c" },
                { "_id": "66736fde17fb868463e7e613", "nombre": "Aureus University School of Medicine", "paisIES": "66736b2317fb868463e7e60c" },
                { "_id": "667371a017fb868463e7e618", "nombre": "Universitat d'Andorra", "paisIES": "6673713717fb868463e7e615" },
                { "_id": "667371ac17fb868463e7e61a", "nombre": "Universitat Oberta de la Salle, Andorra", "paisIES": "6673713717fb868463e7e615" }
            ],
            "message": "Todo ok",
            "error": null
        };

        setNacionalidades(nac.data);
        setFilteredNacionalidades(nac.data);
        setInstituciones(ies.data);
        setFilteredInstituciones(ies.data);
        setLoading(false);
    }, []);

    return (
        <Container>
            <BlurOverlay />
            <Content>
                <Logo src="/path/to/logo.png" alt="Logo" />
                <Description>Registro de Emprendedor</Description>
                <FormContainer>
                    <Form onSubmit={handleSubmit}>
                        <Label htmlFor="nombreEmprendedor">Nombre del Emprendedor</Label>
                        <Input
                            type="text"
                            id="nombreEmprendedor"
                            value={formData.nombreEmprendedor}
                            onChange={handleChange}
                            required
                        />

                        <Label htmlFor="nacionalidad">Nacionalidad</Label>
                        <SelectContainer>
                            <SearchInput
                                type="text"
                                id="searchInputNacionalidad"
                                value={searchTermNacionalidad}
                                onChange={handleSearchChangeNacionalidad}
                                onClick={toggleOptionsNacionalidad}
                                placeholder="Buscar nacionalidad"
                                autoComplete="off"
                            />
                            {searchTermNacionalidad && (
                                <ClearButton onClick={clearNacionalidad}>&times;</ClearButton>
                            )}
                            {isNacionalidadOptionsOpen && (
                                <OptionsList ref={nacionalidadOptionsListRef}>
                                    {filteredNacionalidades.map((nac, index) => (
                                        <OptionItem
                                            key={index}
                                            onClick={() => handleOptionClickNacionalidad(nac.descripcion)}
                                        >
                                            {nac.descripcion}
                                        </OptionItem>
                                    ))}
                                </OptionsList>
                            )}
                        </SelectContainer>

                        <Label htmlFor="ciudadResidencia">Ciudad de Residencia</Label>
                        <Input
                            type="text"
                            id="ciudadResidencia"
                            value={formData.ciudadResidencia}
                            onChange={handleChange}
                            required
                        />

                        <Label htmlFor="documentoIdentidad">Documento de Identidad</Label>
                        <Input
                            type="text"
                            id="documentoIdentidad"
                            value={formData.documentoIdentidad}
                            onChange={handleChange}
                            required
                        />

                        <Label htmlFor="correoPersonal">Correo Personal</Label>
                        <Input
                            type="email"
                            id="correoPersonal"
                            value={formData.correoPersonal}
                            onChange={handleChange}
                            required
                        />

                        <Label htmlFor="correoInstitucional">Correo Institucional</Label>
                        <Input
                            type="email"
                            id="correoInstitucional"
                            value={formData.correoInstitucional}
                            onChange={handleChange}
                            required
                        />

                        <Label htmlFor="telefono">Teléfono</Label>
                        <Input
                            type="text"
                            id="telefono"
                            value={formData.telefono}
                            onChange={handleChange}
                            required
                        />

                        <Label htmlFor="nombreInstitucion">Nombre de la Institución</Label>
                        <SelectContainer>
                            <SearchInput
                                type="text"
                                id="searchInputInstitucion"
                                value={searchTermInstitucion}
                                onChange={handleSearchChangeInstitucion}
                                onClick={toggleOptionsInstitucion}
                                placeholder="Buscar institución"
                                autoComplete="off"
                            />
                            {searchTermInstitucion && (
                                <ClearButton onClick={clearInstitucion}>&times;</ClearButton>
                            )}
                            {isInstitucionOptionsOpen && (
                                <OptionsList ref={institucionOptionsListRef}>
                                    {filteredInstituciones.map((ies, index) => (
                                        <OptionItem
                                            key={index}
                                            onClick={() => handleOptionClickInstitucion(ies.nombre)}
                                        >
                                            {ies.nombre}
                                        </OptionItem>
                                    ))}
                                </OptionsList>
                            )}
                        </SelectContainer>

                        <Label htmlFor="programaAcademico">Programa Académico</Label>
                        <Input
                            type="text"
                            id="programaAcademico"
                            value={formData.programaAcademico}
                            onChange={handleChange}
                            required
                        />

                        <ButtonContainer>
                            <Button type="submit">Registrar</Button>
                            <Button type="button" onClick={handleBack}>Volver</Button>
                        </ButtonContainer>
                    </Form>
                </FormContainer>
            </Content>
        </Container>
    );
};

export default FormEmprendedor;
