import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios'; 
import { BASE_URL } from './App';

const TableContainer = styled.div`
    max-width: 1000px;
    margin: 0 auto;
    padding: 20px;
`;

const Table = styled.table`
    width: 100%;
    border-collapse: separate;
    border-spacing: 0;
    border: 1px solid #213B6B;
    border-radius: 8px;
    overflow: hidden;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 14px;
`;

const Th = styled.th`
    background-color: #213B6B;
    color: white;
    padding: 12px 15px;
    text-align: left;
`;

const Td = styled.td`
    background-color: rgba(33, 59, 107, 0.05);
    color: #333;
    padding: 12px 15px;
    border-bottom: 1px solid #ccc;
`;

const Tr = styled.tr`
    &:last-child td {
        border-bottom: none;
    }
`;

const ListarEmprendimientos = () => {
    const [emprendimientos, setEmprendimientos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`${BASE_URL}/informacionEmprendimiento/obtener`);
                if (!response.data || !response.data.ok || !Array.isArray(response.data.data)) {
                    throw new Error('Formato de datos inesperado para emprendimientos');
                }
                setEmprendimientos(response.data.data);
            } catch (err) {
                setError('Error al cargar los datos: ' + err.message);
                console.error('Error fetching data:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) return <p>Cargando emprendimientos...</p>;
    if (error) return <p>{error}</p>;

    return (
        <TableContainer>
            <Table>
                <thead>
                    <tr>
                        <Th>Fecha</Th>
                        <Th>Estado</Th>
                        <Th>Sector</Th>
                        <Th>Emprendedor</Th>
                        <Th>IES</Th>
                        <Th>Pa√≠s</Th>
                    </tr>
                </thead>
                <tbody>
                    {emprendimientos.length > 0 ? (
                        emprendimientos.map((emprendimiento, index) => (
                            <Tr key={index}>
                                <Td>{emprendimiento.fecha}</Td>
                                <Td>{emprendimiento.estado}</Td>
                                <Td>{emprendimiento.sector?.sector || 'N/A'}</Td>
                                <Td>{`${emprendimiento.infoEmprendedor?.nombres || ''} ${emprendimiento.infoEmprendedor?.apellidos || ''}`}</Td>
                                <Td>{emprendimiento.IES?.nombre || 'N/A'}</Td>
                                <Td>{emprendimiento.pais?.descripcion || 'N/A'}</Td>
                            </Tr>
                        ))
                    ) : (
                        <Tr>
                            <Td colSpan="6">No hay emprendimientos disponibles</Td>
                        </Tr>
                    )}
                </tbody>
            </Table>
        </TableContainer>
    );
};

export default ListarEmprendimientos;
