import React, { useState } from 'react';
import styled from 'styled-components';
import bgImage from './images/habeasdata.png';
import pdfFile from './docs/hd.pdf';

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4);
    backdrop-filter: blur(15px);
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const FormContainer = styled.div`
    width: 90%;
    max-width: 600px;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    @media (max-width: 768px) {
        width: 95%;
    }
    @media (max-width: 480px) {
        width: 100%;
        padding: 10px;
    }
`;

const PDFViewer = styled.iframe`
    width: 100%;
    height: 100%;
    border: none;
`;

const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 5px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const CheckboxContainer = styled.div`
    display: flex;
    align-items: center;
    margin-top: 10px;
`;

const Checkbox = styled.input`
    margin-right: 10px;
`;

const Label = styled.label`
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: ${props => props.disabled ? '#cccccc' : '#213B6B'};
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
    word-wrap: break-word;
    margin: 10px;
    transition: background 0.3s;

    &:hover {
        background: ${props => props.disabled ? '#cccccc' : '#67157D'};
    }
`;

const HabeasData = ({ navigate }) => {
    const [accepted, setAccepted] = useState(false);

    const handleCheckboxChange = (e) => {
        setAccepted(e.target.checked);
    };

    const handleNext = () => {
        if (accepted) {
            navigate('registro_emprendedor');
        }
    };

    return (
        <Container>
            <Content>
                <Description>Habeas Data y Términos y Condiciones</Description>
                <FormContainer>
                    <PDFViewer src={pdfFile} title="Habeas Data PDF" />
                </FormContainer>
                <CheckboxContainer>
                    <Checkbox 
                        type="checkbox" 
                        id="acceptTerms" 
                        checked={accepted} 
                        onChange={handleCheckboxChange}
                    />
                    <Label htmlFor="acceptTerms">Acepto los términos y condiciones</Label>
                </CheckboxContainer>
                <Button onClick={handleNext} disabled={!accepted}>
                    Siguiente
                </Button>
            </Content>
        </Container>
    );
};

export default HabeasData;
