import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios'; 

const TableContainer = styled.div`
    max-width: 800px;
    margin: 0 auto;
    padding: 20px;
`;

const Table = styled.table`
    width: 100%;
    border-collapse: separate;
    border-spacing: 0;
    border: 1px solid #213B6B;
    border-radius: 8px;
    overflow: hidden;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 14px;
`;

const Th = styled.th`
    background-color: #213B6B;
    color: white;
    padding: 10px;
    text-align: left;
`;

const Td = styled.td`
    background-color: rgba(33, 59, 107, 0.05);
    color: #333;
    padding: 8px;
    border-bottom: 1px solid #ccc;

    &:last-child {
        border-bottom: none;
    }
`;

const ListarEvaluadores = () => {
    const [evaluadores, setEvaluadores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // datos del servidor 
    /*useEffect(() => {
        const fetchEvaluadores = async () => {
            try {
                setLoading(true);
                const response = await axios.get('api de consumo');
                setEvaluadores(response.data);
                setLoading(false);
            } catch (err) {
                setError('Error al cargar los evaluadores');
                setLoading(false);
                console.error('Error fetching data:', err);
            }
        };

        fetchEvaluadores();
    }, []);

    if (loading) return <p>Cargando evaluadores...</p>;
    if (error) return <p>{error}</p>; */

    // Simulando la obtención de datos del servidor
    useEffect(() => {
        const fetchData = () => {

            const result = {
                ok: true,
                data: [
                    {
                        _id: "668d9256a9e4591afd2ba890",
                        nombre: "Evaluador 1",
                        idIES: "66736b2317fb868463e7e60c",
                        telefono: "telEval1",
                        correo: "correoEval1@correo.com",
                        idPais: "66736b2317fb868463e7e60c"
                    },
                    {
                        _id: "668d943f6376d4e5b7d5c029",
                        nombre: "Evaluador 2",
                        idIES: "66736fde17fb868463e7e613",
                        telefono: "telEval2",
                        correo: "correoEval2@correo.com",
                        idPais: "66736b2317fb868463e7e60c"
                    }
                ],
                message: "Todo ok",
                error: null
            };

            if (result.ok) {
                setEvaluadores(result.data);
            } else {
                console.error('Error fetching data:', result.message);
            }
        };

        fetchData();
    }, []);  

    return (
        <TableContainer>
            <Table>
                <thead>
                    <tr>
                        <Th>Nombre</Th>
                        <Th>Teléfono</Th>
                        <Th>Correo</Th>
                    </tr>
                </thead>
                <tbody>
                    {evaluadores.map((evaluador) => (
                        <tr key={evaluador._id}>
                            <Td>{evaluador.nombre}</Td>
                            <Td>{evaluador.telefono}</Td>
                            <Td>{evaluador.correo}</Td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </TableContainer>
    );
};

export default ListarEvaluadores;
