import React from 'react';
import styled from 'styled-components';
import { useState , useEffect, useRef } from 'react';
import bgImage from './images/infoemp.png'; 
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { createGlobalStyle } from 'styled-components';
import { BASE_URL } from './App';

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(33, 59, 107, 0.4); /* azul */
    backdrop-filter: blur(15px);
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;
const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 5px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: black;
`;

const FormContainer = styled.div`
    width: 90%;
    max-width: 600px;
    height: 70vh;
    background: rgba(202, 202, 217, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;
    @media (max-width: 768px) {
        width: 95%;
    }
    @media (max-width: 480px) {
        width: 100%;
        padding: 10px;
    }
`;

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
`;


const Label = styled.label`
    font-family: 'Hint Vadodara', sans-serif;
    color: black;
    width: 100%;
    max-width: 400px;
    text-align: left;
`;

const Input = styled.input`
    width: 100%;
    max-width: 400px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
`;

const Select = styled.select`
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    width: 100%;
    max-width: 400px;
    position: relative;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;
    transition: background 0.3s;

    &:hover {
        background: #67157D;
    }
`;

const Option = styled.option`
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 20px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: white; /* Color de fondo */
    width: 50%;
`;

const DatePickerContainer = styled.div`
    width: 100%;
    max-width: 400px;
    margin-bottom: 10px;
`;

const StyledDatePicker = styled(DatePicker)`
    width: 100%;
    max-width: 400px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
    color: black;
    box-sizing: border-box;

    &:focus {
        outline: none;
        border-color: #213B6B;
    }

    // Asegurarse de que el input dentro del DatePicker tenga el ancho completo
    .react-datepicker__input-container input {
        width: 100%;
        height: 100%;
        padding: 0;
        border: none;
        background: transparent;
    }
`;

const GlobalStyle = createGlobalStyle`
    .react-datepicker-wrapper, .react-datepicker__input-container {
        display: block;
        width: 100%;
    }

    .react-datepicker {
        font-family: 'Hint Vadodara', sans-serif;
    }
    .react-datepicker__header {
        background-color: #213B6B;
    }
    .react-datepicker__current-month, .react-datepicker__day-name {
        color: white;
    }
    .react-datepicker__day--selected {
        background-color: #213B6B;
    }
`;


const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 10px;
`;

// Estilo para el párrafo de descripción del sector
const SectorDescription = styled.p`
    font-family: 'Hint Vadodara', sans-serif;
    color: #213B6B;
    font-size: 0.9rem;
    margin-top: 5px;
    text-align: left;
    width: 100%;
    max-width: 400px;
`;
// Estilo para el mensaje de carga
const LoadingMessage = styled.p`
    font-family: 'Hint Vadodara', sans-serif;
    color: #213B6B;
    font-size: 0.9rem;
`;

//ventana flotante
const ModalOverlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(33, 59, 107, 0.8);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1000;
`;

const ModalContent = styled.div`
    background: white;
    padding: 30px;
    border-radius: 10px;
    text-align: center;
    max-width: 400px;
    width: 90%;
    display: flex;
    flex-direction: column;
    align-items: center;
`;

const ModalLogo = styled.img`
    width: 150px;
    height: auto;
    margin-bottom: 20px;
`;

const ModalText = styled.p`
    font-size: 1.2rem;
    margin-bottom: 20px;
    color: #213B6B;
`;

const ModalButton = styled(Button)`
    margin-top: 20px;
`;

const TextArea = styled.textarea`
    width: 100%;
    max-width: 400px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
    resize: vertical;
    min-height: 100px;
`;

const CharCount = styled.div`
    font-size: 0.8rem;
    color: ${props => props.isExceeded ? 'red' : '#666'};
    text-align: right;
    width: 100%;
    max-width: 400px;
    margin-top: 5px;
`;

const FormPage = ({ navigate }) => {
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({});
    const [selectedDate, setSelectedDate] = useState(null);
    const [sectores, setSectores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [descripcionSector, setDescripcionSector] = useState('');

    useEffect(() => {
       // INICIO: Bloque de datos simulados
        // Comentar desde aquí hasta "FIN: Bloque de datos simulados" cuando se implementen datos reales
