        */
        // INICIO: Bloque para obtención de datos reales
        // Descomentar este bloque y comentar el bloque de datos simulados cuando se implemente la obtención de datos reales

        const fetchSectores = async () => {
            try {
                const response = await fetch(`${BASE_URL}/sectorEmprendimiento/obtenerSector`);
                const data = await response.json();
                setSectores(data.data || []);
            } catch (error) {
                console.error('Error fetching sectores:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchSectores();

        // FIN: Bloque para obtención de datos reales
    }, []);

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: value,
        }));

        // Actualizar la descripción del sector y guardar el _id
        if (id === 'sectorIndustria') {
            const selectedSector = sectores.find(sector => sector._id === value);
            if (selectedSector) {
                setDescripcionSector(selectedSector.descripcion);
                setFormData(prevData => ({
                    ...prevData,
                    idSector: selectedSector._id
                }));
            }
        }
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
        setFormData((prevData) => ({
            ...prevData,
            fecha: date,
        }));
    };

    const handleTextAreaChange = (e) => {
        const { id, value } = e.target;
        if (value.length <= 500) {
            setFormData((prevData) => ({
                ...prevData,
                [id]: value,
            }));
        }
    };

    const handleSubmit = async (e) => {
