import React, { useState, useEffect } from 'react';
import { FaLock, FaUser } from 'react-icons/fa';
import styled from 'styled-components';
import bgImage from './images/inicio.png';

const Contenedor = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    width: 100vw;
    background-color: rgba(42, 158, 192, 0.2); 
    position: relative;
`;

const ImagenDeFondo = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
    opacity: 0.1;
`;

const Logo = styled.img`
    position: absolute;
    top: -55px;
    left: 50%;
    transform: translateX(-50%);
    width: 90px;
    height: auto;
`;

const ContenidoLogin = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 400px;
    padding: 30px;
    background: rgba(42, 158, 192, 0.80); 
    border-radius: 20px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    position: relative;
    z-index: 1;
`;

const Titulo = styled.h2`
    margin-bottom: 20px;
    text-align: center;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Etiqueta = styled.label`
    display: block;
    margin-bottom: 5px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const ContenedorInput = styled.div`
    display: flex;
    align-items: center;
    width: 100%;
    margin-bottom: 20px;
    background: #fff;
    border: 1px solid #ccc;
    border-radius: 5px;
    padding: 10px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Entrada = styled.input`
    width: 100%;
    padding: 10px;
    border: none;
    outline: none;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Selector = styled.select`
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    margin-bottom: 20px;
`;

const Boton = styled.button`
    width: 50%;
    padding: 15px;
    background-color: #67157D;
    color: #fff;
    border: none;
    border-radius: 20px;
    cursor: pointer;
    font-size: 16px;
    font-family: 'Hint Vadodara', sans-serif;
    margin: 10px;
    word-wrap: break-word;
`;

const MensajeError = styled.p`
    color: red;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 14px;
    margin-top: 10px;
`;

const PaginaLogin = ({ navigate }) => {
    const [correoElectronico, setCorreoElectronico] = useState('');
    const [documentoIdentidad, setDocumentoIdentidad] = useState('');
    const [perfil, setPerfil] = useState('');
    const [error, setError] = useState('');

    let credenciales = [
        { correoElectronico: 'emprendedor1@example.com', documentoIdentidad: '1234567', perfil: 'emprendedor' },
        { correoElectronico: 'emprendedor2@example.com', documentoIdentidad: '2345678', perfil: 'emprendedor' },
        { correoElectronico: 'emprendedor3@example.com', documentoIdentidad: '3456789', perfil: 'emprendedor' },
        { correoElectronico: 'evaluador1@example.com', documentoIdentidad: '4567890', perfil: 'evaluador' },
        { correoElectronico: 'evaluador2@example.com', documentoIdentidad: '5678901', perfil: 'evaluador' },
        { correoElectronico: 'evaluador3@example.com', documentoIdentidad: '6789012', perfil: 'evaluador' },
    ];

    useEffect(() => {
        // Simulación de obtener credenciales de un servidor.
        // Puedes reemplazar este arreglo con la respuesta de tu servidor.
        // credenciales = fetchCredentialsFromServer(); 
    }, []);

    const manejarSubmit = (e) => {
        e.preventDefault();
        if (correoElectronico === 'admin@admin.com' && documentoIdentidad === 'admin123' && perfil === 'administrador') {
            navigate('inicio_admin');
        } else if (credenciales.some(cred => cred.correoElectronico === correoElectronico && cred.documentoIdentidad === documentoIdentidad && cred.perfil === perfil)) {
            if (perfil === 'emprendedor') {
                navigate('inicio_emprendedor');
            } else if (perfil === 'evaluador') {
                navigate('inicio_evaluador');
            }
        } else {
            setError('Error en las credenciales de acceso');
        }
    };

    return (
        <div>
            <Contenedor>
                <ImagenDeFondo />
                <ContenidoLogin>
                    <Logo src="vela_iush.png" alt="Vela" />
                    <Titulo>Iniciar Sesión</Titulo>
                    <Etiqueta>Perfil</Etiqueta>
                    <Selector value={perfil} onChange={(e) => setPerfil(e.target.value)}>
                        <option value="">Selecciona tu perfil</option>
                        <option value="evaluador">Evaluador</option>
                        <option value="administrador">Administrador</option>
                    </Selector>

                    <Etiqueta>Correo Electrónico</Etiqueta>
                    <ContenedorInput>
                        <Entrada
                            type="email"
                            placeholder="Ingresa tu correo electrónico"
                            value={correoElectronico}
                            onChange={(e) => setCorreoElectronico(e.target.value)}
                        />
                    </ContenedorInput>

                    <Etiqueta>Documento de Identidad</Etiqueta>
                    <ContenedorInput>
                        <Entrada
                            type="text"
                            placeholder="Ingresa tu documento de identidad"
                            value={documentoIdentidad}
                            onChange={(e) => setDocumentoIdentidad(e.target.value)}
                        />
                    </ContenedorInput>

                    {error && <MensajeError>{error}</MensajeError>}

                    <Boton onClick={manejarSubmit}>Login</Boton>
                    <Boton onClick={() => navigate('home')}>Atras</Boton>
                </ContenidoLogin>
            </Contenedor>
        </div>
    );
};

export default PaginaLogin;
