import React from 'react';
import styled from 'styled-components';
import { useState , useEffect, useRef } from 'react';
import bgImage from './fondo_tec.JPG';

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(33, 59, 107, 0.4);
    backdrop-filter: blur(15px);
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const FormContainer = styled.div`
    width: 90%;
    max-width: 600px;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;
    @media (max-width: 768px) {
        width: 95%;
    }
    @media (max-width: 480px) {
        width: 100%;
        padding: 10px;
    }
`;

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
`;

const Label = styled.label`
    font-family: 'Hint Vadodara', sans-serif;
    color: black;
    width: 100%;
    max-width: 400px;
    text-align: left;
`;

const Input = styled.input`
    width: 100%;
    max-width: 400px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
`;

const SelectContainer = styled.div`
    width: 100%;
    max-width: 400px;
    position: relative;
`;

const SearchInput = styled.input`
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
    box-sizing: border-box;
    cursor: pointer;
`;

const OptionsList = styled.div`
    position: absolute;
    width: 100%;
    max-height: 200px;
    overflow-y: auto;
    background-color: white;
    border: 1px solid #ccc;
    border-radius: 5px;
    z-index: 10;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
`;

const OptionItem = styled.div`
    padding: 10px;
    cursor: pointer;
    color: black;
    &:hover {
        background-color: #67157D;
        color: white;
    }
`;

const ClearButton = styled.span`
    position: absolute;
    top: 50%;
    right: 10px;
    transform: translateY(-50%);
    cursor: pointer;
    color: #666;
    font-size: 18px;
`;

const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 5px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 10px;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;
    transition: background 0.3s;

    &:hover {
        background: #67157D;
    }
`;

const FormCrearEvaluador = ({ navigate }) => {
    const [formData, setFormData] = useState({
        nombre: '',
        institucion: '',
        telefono: '',
        correo: '',
        pais: ''
    });

    const [searchTermInstitucion, setSearchTermInstitucion] = useState('');
    const [searchTermPais, setSearchTermPais] = useState('');
    const [filteredInstituciones, setFilteredInstituciones] = useState([]);
    const [filteredPaises, setFilteredPaises] = useState([]);
    const [loading, setLoading] = useState(true);
    const [instituciones, setInstituciones] = useState([]);
    const [paises, setPaises] = useState([]);
    const [isInstitucionOptionsOpen, setIsInstitucionOptionsOpen] = useState(false);
    const [isPaisOptionsOpen, setIsPaisOptionsOpen] = useState(false);

    const institucionOptionsListRef = useRef(null);
    const paisOptionsListRef = useRef(null);

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
    };

    const handleBack = () => {
        navigate('home');
    };

    const handleSearchChangeInstitucion = (e) => {
        const value = e.target.value;
        setSearchTermInstitucion(value);
        setFilteredInstituciones(
            instituciones.filter(ies =>
                ies.nombre.toLowerCase().includes(value.toLowerCase())
            )
        );
    };

    const handleSearchChangePais = (e) => {
        const value = e.target.value;
        setSearchTermPais(value);
        setFilteredPaises(
            paises.filter(pais =>
                pais.descripcion.toLowerCase().includes(value.toLowerCase())
            )
        );
    };

    const handleOptionClickInstitucion = (nombre) => {
        setFormData(prevData => ({
            ...prevData,
            institucion: nombre
        }));
        setSearchTermInstitucion(nombre); // Actualiza el campo con el nombre de la institución seleccionada
        setIsInstitucionOptionsOpen(false);
    };

    const handleOptionClickPais = (descripcion) => {
        setFormData(prevData => ({
            ...prevData,
            pais: descripcion
        }));
        setSearchTermPais(descripcion); // Actualiza el campo con la descripción del país seleccionado
        setIsPaisOptionsOpen(false);
    };

    const toggleOptionsInstitucion = () => {
        setIsInstitucionOptionsOpen(!isInstitucionOptionsOpen);
    };

    const toggleOptionsPais = () => {
        setIsPaisOptionsOpen(!isPaisOptionsOpen);
    };

    const clearInstitucion = () => {
        setFormData(prevData => ({
            ...prevData,
            institucion: ''
        }));
        setSearchTermInstitucion(''); // Limpia el campo de búsqueda de institución
        setFilteredInstituciones(instituciones);
    };

    const clearPais = () => {
        setFormData(prevData => ({
            ...prevData,
            pais: ''
        }));
        setSearchTermPais(''); // Limpia el campo de búsqueda de país
        setFilteredPaises(paises);
    };

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (
                institucionOptionsListRef.current &&
                !institucionOptionsListRef.current.contains(event.target) &&
                !event.target.closest('#searchInputInstitucion')
            ) {
                setIsInstitucionOptionsOpen(false);
            }
            if (
                paisOptionsListRef.current &&
                !paisOptionsListRef.current.contains(event.target) &&
                !event.target.closest('#searchInputPais')
            ) {
                setIsPaisOptionsOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    useEffect(() => {
        let paisesData = {
            "ok": true,
            "data": [
                {
                    "_id": "6673472cf182f9b41b2c1290",
                    "nombre": "COL",
                    "descripcion": "COLOMBIA"
                },
                {
                    "_id": "66736b2317fb868463e7e60c",
                    "nombre": "ABW",
                    "descripcion": "ARUBA"
                },
                {
                    "_id": "6673713717fb868463e7e615",
                    "nombre": "AND",
                    "descripcion": "ANDORRA"
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        let ies = {
            "ok": true,
            "data": [
                {
                    "_id": "66736b9b17fb868463e7e60e",
                    "nombre": "Xavier University School of Medicine, Aruba",
                    "paisIES": "66736b2317fb868463e7e60c"
                },
                {
                    "_id": "66736fd217fb868463e7e611",
                    "nombre": "University of Aruba",
                    "paisIES": "66736b2317fb868463e7e60c"
                },
                {
                    "_id": "66736fde17fb868463e7e613",
                    "nombre": "Aureus University School of Medicine",
                    "paisIES": "66736b2317fb868463e7e60c"
                },
                {
                    "_id": "667371a017fb868463e7e618",
                    "nombre": "Universitat d'Andorra",
                    "paisIES": "6673713717fb868463e7e615"
                },
                {
                    "_id": "667371ac17fb868463e7e61a",
                    "nombre": "Universitat Oberta de la Salle, Andorra",
                    "paisIES": "6673713717fb868463e7e615"
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        const fetchPaises = async () => {
            try {
                const data = paisesData;
                setPaises(data.data || []);
                setFilteredPaises(data.data || []);
            } catch (error) {
                console.error('Error fetching paises:', error);
            } finally {
                setLoading(false);
            }
        };

        const fetchInstituciones = async () => {
            try {
                const data = ies;
                setInstituciones(data.data || []);
                setFilteredInstituciones(data.data || []);
            } catch (error) {
                console.error('Error fetching instituciones:', error);
            }
        };

        fetchPaises();
        fetchInstituciones();
    }, []);

    return (
        <div>
            <Container>
                <BlurOverlay />
                <Content>
                    <Logo src="logo_blanco.png" alt="Logo" />
                    <Description>Formulario de registro de evaluadores</Description>
                    <FormContainer>
                        <Form onSubmit={handleSubmit}>
                            <Label>Nombre:</Label>
                            <Input id="nombre" type="text" value={formData.nombre} onChange={handleChange} required />

                            <Label>Institución a la que pertenece:</Label>
                            <SelectContainer>
                                <SearchInput id="searchInputInstitucion" type="text" value={searchTermInstitucion} onChange={handleSearchChangeInstitucion} onClick={toggleOptionsInstitucion} placeholder="Buscar institución..."  />
                                <ClearButton onClick={clearInstitucion}>&times;</ClearButton>
                                {isInstitucionOptionsOpen && (
                                    <OptionsList ref={institucionOptionsListRef}>
                                        {filteredInstituciones.length > 0 ? (
                                            filteredInstituciones.map((ies) => (
                                                <OptionItem key={ies._id} onClick={() => handleOptionClickInstitucion(ies.nombre)} > {ies.nombre} </OptionItem>
                                            ))
                                        ) : (
                                            <OptionItem>No hay resultados</OptionItem>
                                        )}
                                    </OptionsList>
                                )}
                            </SelectContainer>

                            <Label>Teléfono:</Label>
                            <Input id="telefono" type="text" value={formData.telefono} onChange={handleChange} required />

                            <Label>Correo electrónico:</Label>
                            <Input id="correo" type="email" value={formData.correo} onChange={handleChange} required />

                            <Label>País:</Label>
                            <SelectContainer>
                                <SearchInput id="searchInputPais" type="text" value={searchTermPais} onChange={handleSearchChangePais} onClick={toggleOptionsPais} placeholder="Buscar país..."/>
                                <ClearButton onClick={clearPais}>&times;</ClearButton>
                                {isPaisOptionsOpen && (
                                    <OptionsList ref={paisOptionsListRef}>
                                        {filteredPaises.length > 0 ? (
                                            filteredPaises.map((pais) => (
                                                <OptionItem key={pais._id} onClick={() => handleOptionClickPais(pais.descripcion)} >
                                                    {pais.descripcion}
                                                </OptionItem>
                                            ))
                                        ) : (
                                            <OptionItem>No hay resultados</OptionItem>
                                        )}
                                    </OptionsList>
                                )}
                            </SelectContainer>

                            <ButtonContainer>
                                <Button type="submit">Terminado</Button>
                                <Button type="button" onClick={handleBack}>Regresar</Button>
                            </ButtonContainer>
                        </Form>
                    </FormContainer>
                </Content>
            </Container>
        </div>
    );
};

export default FormCrearEvaluador;
