import React from 'react';
import { FaLock, FaUser } from 'react-icons/fa';
import styled from 'styled-components';
import bgImage from './fondo_login.jpg';

const Container = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    width: 100vw;
    background-color: rgba(103, 21, 125, 0.2); 
    position: relative;
`;

const BackgroundImage = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
    opacity: 0.1;
`;

const Logo = styled.img`
position: absolute;
top: -55px; /* Ajusta este valor según sea necesario para centrar el logo sobre el borde superior */
left: 50%;
transform: translateX(-50%); /* Centra el logo horizontalmente */
width: 90px; /* Ajusta el tamaño del logo según sea necesario */
height: auto; /* Mantiene la proporción del logo */
`;

const LoginPageContent = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 400px;
    padding: 30px;
    background: rgba(255, 255, 255, 0.80); 
    border-radius: 20px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    position: relative;
    z-index: 1;
`;

const Title = styled.h2`
    margin-bottom: 20px;
    text-align: center;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Label = styled.label`
    display: block;
    margin-bottom: 5px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const InputContainer = styled.div`
    display: flex;
    align-items: center;
    width: 100%;
    margin-bottom: 20px;
    background: #fff;
    border: 1px solid #ccc;
    border-radius: 5px;
    padding: 10px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Icon = styled.div`
    margin-right: 10px;
    color: #67157D;
`;

const Input = styled.input`
    width: 100%;
    padding: 10px;
    border: none;
    outline: none;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Button = styled.button`
    width: 50%;
    padding: 15px;
    background-color: #67157D;
    color: #fff;
    border: none;
    border-radius: 20px;
    cursor: pointer;
    font-size: 16px;
    font-family: 'Hint Vadodara', sans-serif;
    margin: 10px; /* Añadido el margen */
    word-wrap: break-word;
`;

const LoginPage = ({navigate}) => {
    const handleInicioClick = () => {
        navigate('home');
    };

    return (
        <div>
        <Container>
        <BackgroundImage />
        <LoginPageContent>
        <Logo src="vela_iush.png" alt="Vela" />
            <Title>Iniciar Sesión</Title>
            <Label>Usuario</Label>
            <InputContainer>
                <Icon>
                    <FaUser />
                </Icon>
                    <Input type="text" placeholder="Ingresa tu usuario" />
            </InputContainer>

            <Label>Contraseña</Label>
            <InputContainer>
                <Icon>
            <FaLock />
                </Icon>
            <Input type="password" placeholder="Ingresa tu contraseña" />
            </InputContainer>

            <Button>Login</Button>
            <Button onClick={handleInicioClick}>Atras</Button>
        </LoginPageContent>
        </Container>
        </div>
    );
};

export default LoginPage;
