import React from 'react';
import styled from 'styled-components';
import bgImage from './fondo_login.jpg'; // Reemplaza con la ruta de tu imagen de fondo
import logo from './logo_blanco.png'; // Reemplaza con la ruta de tu logo

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 100%;
    height: 100%;
    background: rgba(33, 59, 107, 0.4);
    backdrop-filter: blur(15px);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 20px;
`;

const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 20px;
`;

const Title = styled.h1`
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
    font-size: 2rem;
    text-align: center;
    margin: 0;
`;

const AsignacionEmprendimientos = () => {
    return (
        <Container>
            <BlurOverlay>
                <Logo src={logo} alt="Logo" />
                <Title>Asignación de emprendimientos para evaluación</Title>
            </BlurOverlay>
        </Container>
    );
};

export default AsignacionEmprendimientos;
