import React from 'react';
import styled from 'styled-components';
import { useState , useEffect, useRef } from 'react';
import bgImage from './fondo_login.jpg';


// Estilo para el contenedor principal
const Container = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    background-color: #f5f5f5;
`;

// Estilo para el overlay de desenfoque
const BlurOverlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    z-index: 0;
`;

// Estilo para el contenedor del contenido del formulario
const Content = styled.div`
    position: relative;
    z-index: 1;
    background-color: white;
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
    width: 400px;
`;

// Estilo para el logo
const Logo = styled.img`
    display: block;
    margin: 0 auto 10px;
`;

// Estilo para la descripción del formulario
const Description = styled.p`
    text-align: center;
    margin-bottom: 20px;
    font-size: 1.2em;
`;

// Estilo para el contenedor del formulario
const FormContainer = styled.div`
    display: flex;
    flex-direction: column;
    gap: 10px;
`;

// Estilo para el formulario
const Form = styled.form`
    display: flex;
    flex-direction: column;
    gap: 10px;
`;

// Estilo para las etiquetas de los campos del formulario
const Label = styled.label`
    font-weight: bold;
`;

// Estilo para los campos de entrada del formulario
const Input = styled.input`
    padding: 10px;
    border-radius: 5px;
    border: 1px solid #ccc;
    box-sizing: border-box;
`;

// Estilo para el contenedor de los botones
const ButtonContainer = styled.div`
    display: flex;
    gap: 10px;
    justify-content: space-between;
`;

// Estilo para los botones
const Button = styled.button`
    padding: 10px 15px;
    border-radius: 5px;
    border: none;
    color: white;
    background-color: #007bff;
    cursor: pointer;

    &:hover {
        background-color: #0056b3;
    }
`;

// Estilo para el contenedor del select
const SelectContainer = styled.div`
    position: relative;
`;

// Estilo para el campo de búsqueda en el select
const SearchInput = styled.input`
    padding: 10px;
    border-radius: 5px;
    border: 1px solid #ccc;
    box-sizing: border-box;
    width: 100%;
`;

// Estilo para el botón de limpieza
const ClearButton = styled.button`
    position: absolute;
    top: 50%;
    right: 10px;
    transform: translateY(-50%);
    background: none;
    border: none;
    color: #aaa;
    cursor: pointer;

    &:hover {
        color: #333;
    }
`;

// Estilo para la lista de opciones en el select
const OptionsList = styled.div`
    max-height: 160px; /* Ajustado para mostrar máximo 4 elementos */
    overflow-y: auto;
    background-color: white;
    border: 1px solid #ccc;
    border-radius: 5px;
    z-index: 10;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
    top: 100%; /* Posiciona justo debajo del input */
    position: absolute;
    width: 100%;
    max-width: calc(100% - 20px); /* Asegura que el contenedor de opciones no se desborde */
    background: white;
    margin-top: 5px;
`;

// Estilo para cada ítem en la lista de opciones
const OptionItem = styled.div`
    padding: 10px;
    cursor: pointer;

    &:hover {
        background-color: #f0f0f0;
    }
`;

const FormEmprendedor = () => {
    const [formData, setFormData] = useState({
        nombreEmprendedor: '',
        nacionalidad: '',
        ciudadResidencia: '',
        documentoIdentidad: '',
        correoPersonal: '',
        correoInstitucional: '',
        telefono: '',
        nombreInstitucion: '',
        programaAcademico: '',
    });

    const [loading, setLoading] = useState(true);
    const [nacionalidades, setNacionalidades] = useState([]);
    const [filteredNacionalidades, setFilteredNacionalidades] = useState([]);
    const [searchTermNacionalidad, setSearchTermNacionalidad] = useState('');
    const [isNacionalidadOptionsOpen, setIsNacionalidadOptionsOpen] = useState(false);

    const [instituciones, setInstituciones] = useState([]);
    const [filteredInstituciones, setFilteredInstituciones] = useState([]);
    const [searchTermInstitucion, setSearchTermInstitucion] = useState('');
    const [isInstitucionOptionsOpen, setIsInstitucionOptionsOpen] = useState(false);

    const nacionalidadOptionsListRef = useRef(null);
    const institucionOptionsListRef = useRef(null);

    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
    };

    const handleBack = () => {
        console.log('Volver');
    };

    const toggleOptionsNacionalidad = () => {
        setIsNacionalidadOptionsOpen(!isNacionalidadOptionsOpen);
        if (!isNacionalidadOptionsOpen) {
            setSearchTermNacionalidad('');
        }
    };

    const handleOptionClickNacionalidad = (descripcion) => {
        setFormData((prevData) => ({
            ...prevData,
            nacionalidad: descripcion,
        }));
        setSearchTermNacionalidad(descripcion);
        setIsNacionalidadOptionsOpen(false);
    };

    const clearNacionalidad = () => {
        setFormData((prevData) => ({
            ...prevData,
            nacionalidad: '',
        }));
        setSearchTermNacionalidad('');
    };

    const handleSearchChangeNacionalidad = (e) => {
        setSearchTermNacionalidad(e.target.value);
    };

    const toggleOptionsInstitucion = () => {
        setIsInstitucionOptionsOpen(!isInstitucionOptionsOpen);
        if (!isInstitucionOptionsOpen) {
            setSearchTermInstitucion('');
        }
    };

    const handleOptionClickInstitucion = (nombre) => {
        setFormData((prevData) => ({
            ...prevData,
            nombreInstitucion: nombre,
        }));
        setSearchTermInstitucion(nombre);
        setIsInstitucionOptionsOpen(false);
    };

    const clearInstitucion = () => {
        setFormData((prevData) => ({
            ...prevData,
            nombreInstitucion: '',
        }));
        setSearchTermInstitucion('');
    };

    const handleSearchChangeInstitucion = (e) => {
        setSearchTermInstitucion(e.target.value);
    };

    useEffect(() => {
        // Simulación de datos del servidor
        let nac = {
            "ok": true,
            "data": [
                {
                    "_id": "6673472cf182f9b41b2c1290",
                    "nombre": "COL",
                    "descripcion": "COLOMBIA"
                },
                {
                    "_id": "66736b2317fb868463e7e60c",
                    "nombre": "ABW",
                    "descripcion": "ARUBA"
                },
                {
                    "_id": "6673713717fb868463e7e615",
                    "nombre": "AND",
                    "descripcion": "ANDORRA"
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        let ies = {
            "ok": true,
            "data": [
                {
                    "_id": "66736b9b17fb868463e7e60e",
                    "nombre": "Xavier University School of Medicine, Aruba",
                    "paisIES": "66736b2317fb868463e7e60c"
                },
                {
                    "_id": "66736fd217fb868463e7e611",
                    "nombre": "University of Aruba",
                    "paisIES": "66736b2317fb868463e7e60c"
                },
                {
                    "_id": "66736fde17fb868463e7e613",
                    "nombre": "Aureus University School of Medicine",
                    "paisIES": "66736b2317fb868463e7e60c"
                },
                {
                    "_id": "667371a017fb868463e7e618",
                    "nombre": "Universitat d'Andorra",
                    "paisIES": "6673713717fb868463e7e615"
                },
                {
                    "_id": "667371ac17fb868463e7e61a",
                    "nombre": "Universitat Oberta de la Salle, Andorra",
                    "paisIES": "6673713717fb868463e7e615"
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        if (nac.ok) {
            setNacionalidades(nac.data);
            setFilteredNacionalidades(nac.data);
        }

        if (ies.ok) {
            setInstituciones(ies.data);
            setFilteredInstituciones(ies.data);
        }

        setLoading(false);
    }, []);

    useEffect(() => {
        setFilteredNacionalidades(
            nacionalidades.filter(nac =>
                nac.descripcion.toLowerCase().includes(searchTermNacionalidad.toLowerCase())
            )
        );
    }, [searchTermNacionalidad, nacionalidades]);

    useEffect(() => {
        setFilteredInstituciones(
            instituciones.filter(ies =>
                ies.nombre.toLowerCase().includes(searchTermInstitucion.toLowerCase())
            )
        );
    }, [searchTermInstitucion, instituciones]);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (
                nacionalidadOptionsListRef.current &&
                !nacionalidadOptionsListRef.current.contains(event.target) &&
                !event.target.closest('#searchInputNacionalidad')
            ) {
                setIsNacionalidadOptionsOpen(false);
            }

            if (
                institucionOptionsListRef.current &&
                !institucionOptionsListRef.current.contains(event.target) &&
                !event.target.closest('#searchInputInstitucion')
            ) {
                setIsInstitucionOptionsOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    return (
        <Container>
            <BlurOverlay />
            <Content>
                <Logo src="logo.png" alt="Logo" />
                <Description>
                    Registra tu emprendimiento
                </Description>
                <FormContainer>
                    <Form onSubmit={handleSubmit}>
                        <Label htmlFor="nombreEmprendedor">Nombre del Emprendedor</Label>
                        <Input
                            id="nombreEmprendedor"
                            type="text"
                            value={formData.nombreEmprendedor}
                            onChange={handleChange}
                        />
                        <Label htmlFor="nacionalidad">Nacionalidad</Label>
                        <SelectContainer>
                            <SearchInput
                                id="searchInputNacionalidad"
                                type="text"
                                placeholder="Buscar Nacionalidad..."
                                value={searchTermNacionalidad}
                                onChange={handleSearchChangeNacionalidad}
                                onClick={toggleOptionsNacionalidad}
                            />
                            <ClearButton onClick={clearNacionalidad}>×</ClearButton>
                            {isNacionalidadOptionsOpen && (
                                <OptionsList ref={nacionalidadOptionsListRef}>
                                    {filteredNacionalidades.map(nac => (
                                        <OptionItem
                                            key={nac._id}
                                            onClick={() => handleOptionClickNacionalidad(nac.descripcion)}
                                        >
                                            {nac.descripcion}
                                        </OptionItem>
                                    ))}
                                </OptionsList>
                            )}
                        </SelectContainer>
                        <Label htmlFor="ciudadResidencia">Ciudad de Residencia</Label>
                        <Input
                            id="ciudadResidencia"
                            type="text"
                            value={formData.ciudadResidencia}
                            onChange={handleChange}
                        />
                        <Label htmlFor="documentoIdentidad">Documento de Identidad</Label>
                        <Input
                            id="documentoIdentidad"
                            type="text"
                            value={formData.documentoIdentidad}
                            onChange={handleChange}
                        />
                        <Label htmlFor="correoPersonal">Correo Personal</Label>
                        <Input
                            id="correoPersonal"
                            type="email"
                            value={formData.correoPersonal}
                            onChange={handleChange}
                        />
                        <Label htmlFor="correoInstitucional">Correo Institucional</Label>
                        <Input
                            id="correoInstitucional"
                            type="email"
                            value={formData.correoInstitucional}
                            onChange={handleChange}
                        />
                        <Label htmlFor="telefono">Teléfono</Label>
                        <Input
                            id="telefono"
                            type="tel"
                            value={formData.telefono}
                            onChange={handleChange}
                        />
                        <Label htmlFor="nombreInstitucion">Nombre de la Institución</Label>
                        <SelectContainer>
                            <SearchInput
                                id="searchInputInstitucion"
                                type="text"
                                placeholder="Buscar Institución..."
                                value={searchTermInstitucion}
                                onChange={handleSearchChangeInstitucion}
                                onClick={toggleOptionsInstitucion}
                            />
                            <ClearButton onClick={clearInstitucion}>×</ClearButton>
                            {isInstitucionOptionsOpen && (
                                <OptionsList ref={institucionOptionsListRef}>
                                    {filteredInstituciones.map(ies => (
                                        <OptionItem
                                            key={ies._id}
                                            onClick={() => handleOptionClickInstitucion(ies.nombre)}
                                        >
                                            {ies.nombre}
                                        </OptionItem>
                                    ))}
                                </OptionsList>
                            )}
                        </SelectContainer>
                        <Label htmlFor="programaAcademico">Programa Académico</Label>
                        <Input
                            id="programaAcademico"
                            type="text"
                            value={formData.programaAcademico}
                            onChange={handleChange}
                        />
                        <ButtonContainer>
                            <Button type="button" onClick={handleBack}>
                                Volver
                            </Button>
                            <Button type="submit">Registrar</Button>
                        </ButtonContainer>
                    </Form>
                </FormContainer>
            </Content>
        </Container>
    );
};

export default FormEmprendedor;
