import React from 'react';
import styled from 'styled-components';
import bgImage from './fondocongreso.png'; 

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4);
    backdrop-filter: blur(15px);
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const Logo = styled.img`
    width: 150px;
    height: auto;
    position: absolute;
    top: 20px;
    left: calc(30% + 20px); // 30% is the width of the non-blurred area
    z-index: 2;
`;

const StartupLogo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 20px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 20px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Button = styled.button`
    width: 250px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;
`;

const LoginButton = styled(Button)`
    position: absolute;
    top: 20px;
    right: 20px;
    width: 150px;
`;

const HomePage = ({ navigate }) => {
    const handleLoginClick = () => {
        navigate('login');
    };

    const handleFormEmprendedorClick = () => {
        navigate('registro_emprendedor');
    };

    return (
        <div>
            <Container>
                <BlurOverlay />
                <Logo src="logo.png" alt="Logo" />
                <Content>
                    <StartupLogo src="startup_iush.png" alt="Startup IUSH Logo" />
                    <Description>Congreso iberoamericano de emprendimiento</Description>
                    <Button onClick={handleFormEmprendedorClick}>Registrar Emprendedor</Button>
                </Content>
                <LoginButton onClick={handleLoginClick}>Login</LoginButton>
            </Container>
        </div>
    );
};

export default HomePage;
