import React from 'react';
import styled from 'styled-components';
import { useState , useEffect, useRef } from 'react';
import bgImage from './fondo_login.jpg'; // Reemplaza con la ruta de tu imagen de fondo
import logo from './logo_blanco.png'; // Reemplaza con la ruta de tu logo

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 100%;
    height: 100%;
    background: rgba(33, 59, 107, 0.4);
    backdrop-filter: blur(15px);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 20px;
`;

const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 20px;
`;

const Title = styled.h1`
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
    font-size: 2rem;
    text-align: center;
    margin: 0;
`;

const ContentContainer = styled.div`
    display: flex;
    flex-direction: row;
    width: 70%;
    height: 60%;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    max-height: 60%; /* Asegura que el contenedor no sea más alto que el 60% de la pantalla */
    @media (max-width: 768px) {
        width: 90%;
    }
    @media (max-width: 480px) {
        flex-direction: column;
        height: auto;
    }
`;

const EvaluadoresContainer = styled.div`
    flex: 1;
    padding: 10px;
    overflow-y: auto;
    border-right: 1px solid #ccc;
    max-height: 100%;
    @media (max-width: 480px) {
        border-right: none;
        border-bottom: 1px solid #ccc;
    }
`;

const EmprendimientosContainer = styled.div`
    flex: 1;
    padding: 10px;
    overflow-y: auto;
    max-height: 100%;
    @media (max-width: 480px) {
        border-top: 1px solid #ccc;
    }
`;

const EvaluadorItem = styled.div`
    padding: 10px;
    cursor: ${props => (props.disabled ? 'not-allowed' : 'pointer')};
    color: ${props => (props.selected ? 'white' : 'black')};
    background: ${props => (props.selected ? '#67157D' : props.disabled ? '#dcdcdc' : 'white')};
    &:hover {
        background-color: ${props => (props.disabled ? '#dcdcdc' : '#67157D')};
        color: ${props => (props.disabled ? 'black' : 'white')};
    }
    margin-bottom: 10px;
    border-radius: 5px;
`;

const EmprendimientoItem = styled.div`
    padding: 10px;
    cursor: ${props => (props.disabled ? 'not-allowed' : 'pointer')};
    color: ${props => (props.selected ? 'white' : 'black')};
    background: ${props => (props.selected ? '#67157D' : props.disabled ? '#dcdcdc' : 'white')};
    &:hover {
        background-color: ${props => (props.disabled ? '#dcdcdc' : '#67157D')};
        color: ${props => (props.disabled ? 'black' : 'white')};
    }
    margin-bottom: 10px;
    border-radius: 5px;
`;

const ButtonContainer = styled.div`
    margin-top: 20px;
    display: flex;
    flex-direction: row;
    gap: 10px;
`;

const Button = styled.button`
    padding: 10px 20px;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 1rem;
    font-weight: 400;
    cursor: pointer;
    transition: background 0.3s;
    &:hover {
        background: #67157D;
    }
`;

const TableContainer = styled.div`
    margin-top: 20px;
    width: 70%;
    max-height: 50%; /* Aumentar el tamaño máximo del contenedor de la tabla */
    overflow-y: auto;
    background: rgba(255, 255, 255, 0.9);
    padding: 10px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    @media (max-width: 768px) {
        width: 90%;
    }
    @media (max-width: 480px) {
        max-height: 30%;
    }
`;

const Table = styled.table`
    width: 100%;
    border-collapse: collapse;
`;

const TableHeader = styled.th`
    background: #213B6B;
    color: white;
    padding: 10px;
    border: 1px solid #ccc;
    text-align: left;
`;

const TableData = styled.td`
    padding: 10px;
    border: 1px solid #ccc;
    text-align: left;
`;

const DeleteButton = styled.button`
    padding: 5px 10px;
    border-radius: 5px;
    border: none;
    background: #e74c3c;
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 0.8rem;
    cursor: pointer;
    transition: background 0.3s;
    &:hover {
        background: #c0392b;
    }
`;

const AsignacionEmprendimientos = () => {
    const [evaluadores, setEvaluadores] = useState([]);
    const [emprendimientos, setEmprendimientos] = useState([]);
    const [selectedEvaluador, setSelectedEvaluador] = useState(null);
    const [selectedEmprendimientos, setSelectedEmprendimientos] = useState([]);
    const [assignments, setAssignments] = useState([]);
    const [disabledEvaluadores, setDisabledEvaluadores] = useState([]);
    const [disabledEmprendimientos, setDisabledEmprendimientos] = useState([]);

    const fetchEvaluadores = async () => {
        // Datos de ejemplo para evaluadores
        const data = [
            { id: 1, nombre: 'evaluador 1' },
            { id: 2, nombre: 'evaluador 2' },
            { id: 3, nombre: 'evaluador 3' },
        ];
        setEvaluadores(data);
    };

    const fetchEmprendimientos = async () => {
        // Datos de ejemplo para emprendimientos
        const data = [
            { id: 1, nombre: 'Emprendimiento 1' },
            { id: 2, nombre: 'Emprendimiento 2' },
            { id: 3, nombre: 'Emprendimiento 3' },
        ];
        setEmprendimientos(data);
    };

    useEffect(() => {
        fetchEvaluadores();
        fetchEmprendimientos();
    }, []);

    const handleEvaluadorClick = (evaluador) => {
        if (disabledEvaluadores.includes(evaluador.id)) return;
        setSelectedEvaluador(evaluador);
        console.log(`Evaluador seleccionado: ${evaluador.nombre}`);
    };

    const handleEmprendimientoClick = (emprendimientoId) => {
        if (disabledEmprendimientos.includes(emprendimientoId)) return;
        setSelectedEmprendimientos(prev => {
            const newSelection = prev.includes(emprendimientoId) ? prev.filter(id => id !== emprendimientoId) : [...prev, emprendimientoId];
            console.log(`Emprendimiento ${newSelection.includes(emprendimientoId) ? 'seleccionado' : 'desmarcado'}: ${emprendimientoId}`);
            return newSelection;
        });
    };

    const handleAsignar = () => {
        if (selectedEvaluador && selectedEmprendimientos.length > 0) {
            const newAssignment = {
                evaluador: selectedEvaluador.nombre,
                emprendimientos: selectedEmprendimientos.map(id => emprendimientos.find(e => e.id === id).nombre),
                evaluadorId: selectedEvaluador.id,
                emprendimientos: selectedEmprendimientos.map(id => emprendimientos.find(e => e.id === id)),
            };
            setAssignments(prev => [...prev, newAssignment]);
            setDisabledEvaluadores(prev => [...prev, selectedEvaluador.id]);
            setDisabledEmprendimientos(prev => [...prev, ...selectedEmprendimientos]);
            setSelectedEvaluador(null);
            setSelectedEmprendimientos([]);
            console.log('Asignación realizada:', newAssignment);
        } else {
            console.error('Debes seleccionar un evaluador y al menos un emprendimiento');
        }
    };

    const handlelimpiar = () => {
        setSelectedEvaluador(null);
        setSelectedEmprendimientos([]);
        setAssignments([]);
        setDisabledEvaluadores([]);
        setDisabledEmprendimientos([]);
        console.log('Selecciones y asignaciones limpiadas');
    };

    const handleDeleteAssignment = (index, evaluadorId, emprendimientos) => {
        setAssignments(prev => prev.filter((_, i) => i !== index));
        setDisabledEvaluadores(prev => prev.filter(id => id !== evaluadorId));
        setDisabledEmprendimientos(prev => {
            const emprsToReenable = emprendimientos.map(e => e.id);
            return prev.filter(id => !emprsToReenable.includes(id));
        });
        console.log('Asignación eliminada:', { evaluadorId, emprendimientos });
    };

    const handleInicioAdminClick = () => {
        navigate('inicio_admin');
    };


    return (
        <Container>
            <BlurOverlay>
                <Logo src={logo} alt="Logo" />
                <Title>Asignación de Emprendimientos para Evaluación</Title>
                <ContentContainer>
                    <EvaluadoresContainer>
                        <h2>Evaluadores</h2>
                        {evaluadores.map(evaluador => (
                            <EvaluadorItem
                                key={evaluador.id}
                                onClick={() => handleEvaluadorClick(evaluador)}
                                selected={selectedEvaluador?.id === evaluador.id}
                                disabled={disabledEvaluadores.includes(evaluador.id)}
                            >
                                {evaluador.nombre}
                            </EvaluadorItem>
                        ))}
                    </EvaluadoresContainer>
                    <EmprendimientosContainer>
                        <h2>Emprendimientos</h2>
                        {emprendimientos.map(emprendimiento => (
                            <EmprendimientoItem
                                key={emprendimiento.id}
                                onClick={() => handleEmprendimientoClick(emprendimiento.id)}
                                selected={selectedEmprendimientos.includes(emprendimiento.id)}
                                disabled={disabledEmprendimientos.includes(emprendimiento.id)}
                            >
                                {emprendimiento.nombre}
                            </EmprendimientoItem>
                        ))}
                    </EmprendimientosContainer>
                </ContentContainer>
                <ButtonContainer>
                    <Button onClick={handleAsignar}>Asignar</Button>
                    <Button onClick={handlelimpiar}>Limpiar Todo</Button>
                    <Button onClick={handleInicioAdminClick}>Regresar</Button>
                </ButtonContainer>
                {assignments.length > 0 && (
                    <TableContainer>
                        <Table>
                            <thead>
                                <tr>
                                    <TableHeader>Evaluador</TableHeader>
                                    <TableHeader>Emprendimientos</TableHeader>
                                    <TableHeader>Acción</TableHeader>
                                </tr>
                            </thead>
                            <tbody>
                                {assignments.map((assignment, index) => (
                                    <tr key={index}>
                                        <TableData>{assignment.evaluador}</TableData>
                                        <TableData>{assignment.emprendimientos.map(e => e.nombre).join(', ')}</TableData>
                                        <TableData>
                                            <DeleteButton onClick={() => handleDeleteAssignment(index, assignment.evaluadorId, assignment.emprendimientos)}>Eliminar</DeleteButton>
                                        </TableData>
                                    </tr>
                                ))}
                            </tbody>
                        </Table>
                    </TableContainer>
                )}
            </BlurOverlay>
        </Container>
    );
};

export default AsignacionEmprendimientos;
