    font-size: 2rem;
    text-align: center;
    margin: 0;
`;

const ContentContainer = styled.div`
    display: flex;
    flex-direction: row;
    width: 70%;
    height: 60%;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    max-height: 60%;
    @media (max-width: 768px) {
        width: 90%;
    }
    @media (max-width: 480px) {
        flex-direction: column;
        height: auto;
    }
`;

const EvaluadoresContainer = styled.div`
    flex: 1;
    padding: 10px;
