    }
`;

const EmprendimientoItem = styled.div`
    padding: 10px;
    cursor: ${props => (props.disabled ? 'not-allowed' : 'pointer')};
    color: ${props => (props.selected ? 'white' : 'black')};
    background: ${props => (props.selected ? '#67157D' : props.disabled ? '#dcdcdc' : 'white')};
