    const handleEvaluadorChange = (e) => {
        setSelectedEvaluador(e.target.value);
        setSelectedEmprendimientos([]);
    };

    const handleEmprendimientoClick = (emprendimientoId) => {
        if (disabledEmprendimientos.includes(emprendimientoId)) return;
        setSelectedEmprendimientos(prev =>
            prev.includes(emprendimientoId)
                ? prev.filter(id => id !== emprendimientoId)
                : [...prev, emprendimientoId]
        );
    };

    const handleAsignar = () => {
        if (selectedEvaluador && selectedEmprendimientos.length > 0) {
            const evaluador = evaluadores.find(e => e._id === selectedEvaluador);
            const asignadosEmprendimientos = emprendimientos.filter(e => selectedEmprendimientos.includes(e._id));

            const newAssignment = {
                evaluador: evaluador.nombre,
                evaluadorId: evaluador._id,
                emprendimientos: asignadosEmprendimientos
            };

            setAssignments(prev => [...prev, newAssignment]);
            setDisabledEvaluadores(prev => [...prev, evaluador._id]);
            setDisabledEmprendimientos(prev => [...prev, ...selectedEmprendimientos]);
            setSelectedEvaluador('');
            setSelectedEmprendimientos([]);
        } else {
            console.error('Debes seleccionar un evaluador y al menos un emprendimiento');
        }
    };

    const handlelimpiar = () => {
        setSelectedEvaluador('');
        setSelectedEmprendimientos([]);
        setAssignments([]);
        setDisabledEvaluadores([]);
        setDisabledEmprendimientos([]);
    };

    const handleDeleteAssignment = (index, evaluadorId, emprendimientos) => {
        setAssignments(prev => prev.filter((_, i) => i !== index));
        setDisabledEvaluadores(prev => prev.filter(id => id !== evaluadorId));
        setDisabledEmprendimientos(prev => {
            const emprsToReenable = emprendimientos.map(e => e._id);
            return prev.filter(id => !emprsToReenable.includes(id));
        });
    };

