import React, { useState, useEffect } from 'react';
import { FaLock, FaUser } from 'react-icons/fa';
import styled from 'styled-components';
import bgImage from './fondo_login.jpg';

const Contenedor = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    width: 100vw;
    background-color: rgba(103, 21, 125, 0.2); 
    position: relative;
`;

const ImagenDeFondo = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
    opacity: 0.1;
`;

const Logo = styled.img`
    position: absolute;
    top: -55px; /* Ajusta este valor según sea necesario para centrar el logo sobre el borde superior */
    left: 50%;
    transform: translateX(-50%); /* Centra el logo horizontalmente */
    width: 90px; /* Ajusta el tamaño del logo según sea necesario */
    height: auto; /* Mantiene la proporción del logo */
`;

const ContenidoLogin = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 400px;
    padding: 30px;
    background: rgba(255, 255, 255, 0.80); 
    border-radius: 20px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    position: relative;
    z-index: 1;
`;

const Titulo = styled.h2`
    margin-bottom: 20px;
    text-align: center;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Etiqueta = styled.label`
    display: block;
    margin-bottom: 5px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const ContenedorInput = styled.div`
    display: flex;
    align-items: center;
    width: 100%;
    margin-bottom: 20px;
    background: #fff;
    border: 1px solid #ccc;
    border-radius: 5px;
    padding: 10px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Icono = styled.div`
    margin-right: 10px;
    color: #67157D;
`;

const Entrada = styled.input`
    width: 100%;
    padding: 10px;
    border: none;
    outline: none;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Selector = styled.select`
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    margin-bottom: 20px;
`;

const Boton = styled.button`
    width: 50%;
    padding: 15px;
    background-color: #67157D;
    color: #fff;
    border: none;
    border-radius: 20px;
    cursor: pointer;
    font-size: 16px;
    font-family: 'Hint Vadodara', sans-serif;
    margin: 10px; /* Añadido el margen */
    word-wrap: break-word;
`;

const MensajeError = styled.p`
    color: red;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 14px;
    margin-top: 10px;
`;

const PaginaLogin = ({ navigate }) => {
    const [usuario, setUsuario] = useState('');
    const [contrasena, setContrasena] = useState('');
    const [perfil, setPerfil] = useState('');
    const [error, setError] = useState('');

    // Aquí se define la variable let con los usuarios y contraseñas.
    let credenciales = [
        { usuario: 'emprendedor1', contrasena: 'clave1', perfil: 'emprendedor' },
        { usuario: 'emprendedor2', contrasena: 'clave2', perfil: 'emprendedor' },
        { usuario: 'emprendedor3', contrasena: 'clave3', perfil: 'emprendedor' },
        { usuario: 'evaluador1', contrasena: 'clave4', perfil: 'evaluador' },
        { usuario: 'evaluador2', contrasena: 'clave5', perfil: 'evaluador' },
        { usuario: 'evaluador3', contrasena: 'clave6', perfil: 'evaluador' },
    ];

    useEffect(() => {
        // Simulación de obtener credenciales de un servidor.
        // Puedes reemplazar este arreglo con la respuesta de tu servidor.
        // credenciales = fetchCredentialsFromServer(); 
    }, []);

    const manejarSubmit = (e) => {
        e.preventDefault();
        if (usuario === 'admin' && contrasena === 'admin' && perfil === 'administrador') {
            navigate('inicio_admin');
        } else if (credenciales.some(cred => cred.usuario === usuario && cred.contrasena === contrasena && cred.perfil === perfil)) {
            if (perfil === 'emprendedor') {
                navigate('inicio_emprendedor');
            } else if (perfil === 'evaluador') {
                navigate('inicio_evaluador');
            }
        } else {
            setError('Error en las credenciales de acceso');
        }
    };

    return (
        <div>
            <Contenedor>
                <ImagenDeFondo />
                <ContenidoLogin>
                    <Logo src="vela_iush.png" alt="Vela" />
                    <Titulo>Iniciar Sesión</Titulo>
                    <Etiqueta>Perfil</Etiqueta>
                    <Selector value={perfil} onChange={(e) => setPerfil(e.target.value)}>
                        <option value="">Selecciona tu perfil</option>
                        <option value="emprendedor">Emprendedor</option>
                        <option value="evaluador">Evaluador</option>
                        <option value="administrador">Administrador</option>
                    </Selector>

                    <Etiqueta>Usuario</Etiqueta>
                    <ContenedorInput>
                        <Icono>
                            <FaUser />
                        </Icono>
                        <Entrada
                            type="text"
                            placeholder="Ingresa tu usuario"
                            value={usuario}
                            onChange={(e) => setUsuario(e.target.value)}
                        />
                    </ContenedorInput>

                    <Etiqueta>Contraseña</Etiqueta>
                    <ContenedorInput>
                        <Icono>
                            <FaLock />
                        </Icono>
                        <Entrada
                            type="password"
                            placeholder="Ingresa tu contraseña"
                            value={contrasena}
                            onChange={(e) => setContrasena(e.target.value)}
                        />
                    </ContenedorInput>

                    {error && <MensajeError>{error}</MensajeError>}

                    <Boton onClick={manejarSubmit}>Login</Boton>
                    <Boton onClick={() => navigate('home')}>Atras</Boton>
                </ContenidoLogin>
            </Contenedor>
        </div>
    );
};

export default PaginaLogin;
