import React from 'react';
import styled from 'styled-components';
import bgImage from './fondo_iush.jpg'; // Ruta correcta de tu imagen

const Container = styled.div`
    display: flex;
    height: 100vh;
    height: 100vh; /* Full viewport height */
    width: 100vw; /* Full viewport width */
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 50%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4); /* Añadida la transparencia del 20% */
    backdrop-filter: blur(15px);

`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center; /* Alinea al centro horizontalmente */
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 50%; /* Ancho del contenido */
    margin-left: auto;
`;

const Logo = styled.img`
    width: 200px; /* Ajusta el ancho según sea necesario */
    height: auto; /* Mantén la proporción de la imagen */
    margin-bottom: 2px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center; /* Alinea al centro horizontalmente */
    margin-bottom: 20px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Button = styled.button`
width: 150px;
height: 50px;
padding: 0px 15px;
display: flex;
justify-content: center;
align-items: center;
border-radius: 50px; /* Cambiado de 23px a 50px */
border: none; /* Eliminado el borde */
background: #213B6B; /* Cambiado el fondo */
box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3); /* Añadido el box-shadow */
color: white; /* Color del texto */
font-family: 'Hint Vadodara', sans-serif;
font-size: 20px; /* Cambiado el tamaño de fuente */
font-weight: 400; /* Cambiado el peso de la fuente */
line-height: normal;
cursor: pointer;
word-wrap: break-word; /* Añadido word-wrap */
margin: 10px; /* Añadido el margen */
`;


const HomePage = ({ navigate }) => {
    const handleLoginClick = () => {
        navigate('login');
    };

    const handleFormEmpClick = () => {
        navigate('registro_emp');
    };

    const handleFormEmprendedorClick = () => {
        navigate('registro_emprendedor');
    };

    return (
        <div>
        <Container>
            <BlurOverlay />
        <Content>
        <Logo src="logo.png" alt="Logo" />
            <Description>Congreso iberoamericano de emprendimiento</Description>
            <Button onClick={handleLoginClick}>Login</Button>
            <Button onClick={handleFormEmpClick}>Registro</Button>
            <Button onClick={handleFormEmprendedorClick}>Emprendedor</Button>
            <Button onClick={handleLoginClick}>Calificaciones</Button>
        </Content>
    </Container>
    </div>
    );
};


export default HomePage;
