import React from 'react';
import styled from 'styled-components';
import bgImage from './fondo_admin.jpg'; // Asegúrate de tener la imagen de fondo

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(33, 59, 107, 0.4);
    backdrop-filter: blur(15px);
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 5px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const FormContainer = styled.div`
    width: 90%;
    max-width: 600px;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;

    @media (max-width: 768px) {
        width: 95%;
    }

    @media (max-width: 480px) {
        width: 100%;
        padding: 10px;
    }
`;

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
`;

const Label = styled.label`
    font-family: 'Hint Vadodara', sans-serif;
    color: black;
    width: 100%;
    max-width: 400px;
    text-align: left;
`;

const Input = styled.input`
    width: 100%;
    max-width: 400px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
`;

const Select = styled.select`
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    width: 100%;
    max-width: 400px;
    position: relative;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;
    transition: background 0.3s;

    &:hover {
        background: #67157D;
    }
`;

const Option = styled.option`
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 20px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #67157D; /* Color de fondo */
    width: 50%;
`;

const DatePickerContainer = styled.div`
    margin-bottom: 10px;
    width: 100%;
    max-width: 400px;
    position: relative;
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 10px;
`;

const InicioEmprendedor = () => {
    return (
        <Container>
            <BlurOverlay>
                <Content>
                    <Logo src="path-to-your-logo.png" alt="Logo" />
                    <Description>Hola emprendedor, registra tus emprendimientos aquí</Description>
                    <FormContainer>
                        <Form>
                            <Label htmlFor="nombre">Nombre</Label>
                            <Input type="text" id="nombre" name="nombre" required />
                            <Label htmlFor="descripcion">Descripción</Label>
                            <Input type="text" id="descripcion" name="descripcion" required />
                            <Label htmlFor="fecha">Fecha</Label>
                            <DatePickerContainer>
                                <Input type="date" id="fecha" name="fecha" required />
                            </DatePickerContainer>
                            <Button type="submit">Registrar</Button>
                            <Button onClick={handleInicioClick}>Cancelar</Button>
                        </Form>
                    </FormContainer>
                </Content>
            </BlurOverlay>
        </Container>
    );
};

export default InicioEmprendedor;
