import React, { useState } from 'react';
import bgImage from './fondo_login.jpg';
import pdfFile from './hd.pdf';
import './styles/HabeasData.css';

const HabeasData = ({ navigate }) => {
    const [accepted, setAccepted] = useState(false);

    const handleCheckboxChange = (e) => {
        setAccepted(e.target.checked);
    };

    const handleNext = () => {
        if (accepted) {
            navigate('registro_emprendedor');
        }
    };

    return (
        <div className="container" style={{backgroundImage: `url(${bgImage})`}}>
            <div className="blur-overlay" />
            <div className="content">
                <img className="logo" src="logo.png" alt="Logo" />
                <p className="description">Habeas Data y Términos y Condiciones</p>
                <div className="form-container">
                    <div className="pdf-overlay" />
                    <iframe className="pdf-viewer" src={pdfFile} title="Habeas Data PDF" />
                </div>
                <div className="checkbox-container">
                    <input 
                        className="checkbox"
                        type="checkbox" 
                        id="acceptTerms" 
                        checked={accepted} 
                        onChange={handleCheckboxChange}
                    />
                    <label className="label" htmlFor="acceptTerms">Acepto los términos y condiciones</label>
                </div>
                <button 
                    className="button" 
                    onClick={handleNext} 
                    disabled={!accepted}
                >
                    Siguiente
                </button>
            </div>
        </div>
    );
};

export default HabeasData;
