import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios'; 
import { BASE_URL } from './App';

const TableContainer = styled.div`
    max-width: 800px;
    margin: 0 auto;
    padding: 20px;
`;

const Table = styled.table`
    width: 100%;
    border-collapse: separate;
    border-spacing: 0;
    border: 1px solid #213B6B;
    border-radius: 8px;
    overflow: hidden;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 14px;
`;

const Th = styled.th`
    background-color: #213B6B;
    color: white;
    padding: 10px;
    text-align: left;
`;

const Td = styled.td`
    background-color: rgba(33, 59, 107, 0.05);
    color: #333;
    padding: 8px;
    border-bottom: 1px solid #ccc;

    &:last-child {
        border-bottom: none;
    }
`;

const ListarEvaluadores = () => {
    const [evaluadores, setEvaluadores] = useState([]);


    useEffect(() => {
        const fetchEvaluadores = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`${BASE_URL}/evaluador/obtener`);

                if (response.data && Array.isArray(response.data.data)) {
                    setEvaluadores(response.data.data);
                } else {
                    throw new Error('Unexpected data format from API');
                }
            } catch (err) {
                setError('Error al cargar los evaluadores: ' + err.message);
                console.error('Error fetching data:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchEvaluadores();
    }, []);

    if (loading) return <p>Cargando evaluadores...</p>;
    if (error) return <p>{error}</p>; 

    return (
        <TableContainer>
            <Table>
                <thead>
                    <tr>
                        <Th>Nombre</Th>
                        <Th>Tel√©fono</Th>
                        <Th>Correo</Th>
                    </tr>
                </thead>
                <tbody>
                    {evaluadores.length > 0 ? (
                        evaluadores.map((evaluador) => (
                            <tr key={evaluador._id}>
                                <Td>{evaluador.nombre}</Td>
                                <Td>{evaluador.telefono}</Td>
                                <Td>{evaluador.correo}</Td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <Td colSpan="3">No hay evaluadores disponibles</Td>
                        </tr>
                    )}
                </tbody>
            </Table>
        </TableContainer>
    );
};

export default ListarEvaluadores;
