import React, { useState } from 'react';
import styled from 'styled-components';
import bgImage from './fondocongreso.png'; 

const Container = styled.div`
    display: flex;
    min-height: 100vh;
    width: 100%;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4);
    backdrop-filter: blur(15px);

    @media (max-width: 768px) {
        width: 100%;
    }
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;

    @media (max-width: 768px) {
        width: 100%;
        margin: 0;
        padding-top: 80px;
    }
`;

const Logo = styled.img`
    width: 150px;
    height: auto;
    position: absolute;
    top: 20px;
    left: calc(30% + 20px);
    z-index: 2;

    @media (max-width: 768px) {
        left: 20px;
        width: 100px;
    }
`;

const StartupLogo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 20px;

    @media (max-width: 768px) {
        width: 150px;
    }
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 20px;
    font-family: 'Hint Vadodara', sans-serif;

    @media (max-width: 768px) {
        font-size: 1.2rem;
    }
`;

const Button = styled.button`
    width: 250px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;

    @media (max-width: 768px) {
        width: 200px;
        font-size: 16px;
    }
`;

const LoginButton = styled(Button)`
    position: absolute;
    top: 20px;
    right: 20px;
    width: 150px;

    @media (max-width: 768px) {
        top: 20px;
        right: 20px;
        width: 100px;
        height: 40px;
        font-size: 14px;
    }
`;

const ErrorText = styled.p`
    color: #ff6b6b;
    font-size: 1rem;
    margin-bottom: 20px;
`;

const HomePage = ({ navigate }) => {
    const [startupLogoError, setStartupLogoError] = useState(false);

    const handleLoginClick = () => {
        navigate('login');
    };

    const handleFormEmprendedorClick = () => {
        navigate('registro_emprendedor');
    };

    const handleStartupLogoError = () => {
        console.error("No se pudo cargar startupiush.png");
        setStartupLogoError(true);
    };

    return (
        <div>
            <Container>
                <BlurOverlay />
                <Logo src="logo.png" alt="Logo" onError={() => console.error("No se pudo cargar logo.png")} />
                <Content>
                    {!startupLogoError ? (
                        <StartupLogo 
                            src="startupiush.png" 
                            alt="Logo Startup IUSH" 
                            onError={handleStartupLogoError}
                        />
                    ) : (
                        <ErrorText>No se pudo cargar el logo de Startup IUSH</ErrorText>
                    )}
                    <Description>Congreso iberoamericano de emprendimiento</Description>
                    <Button onClick={handleFormEmprendedorClick}>Registrar Emprendedor</Button>
                    <LoginButton onClick={handleLoginClick}>Login</LoginButton>
                </Content>              
            </Container>
        </div>
    );
};

export default HomePage;
