import React, { useState } from 'react';
import styled from 'styled-components';
import bgImage from './images/inicio.png'; 

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const Button = styled.button`
    width: 250px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: ${props => props.disabled ? '#ccc' : props.active ? '#5f92bb' : '#2c5c85'};
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;

    &:hover {
        background: ${props => props.disabled ? '#ccc' : '#8cb8d3'};
    }
    `;

const LoginButton = styled(Button)`
    position: absolute;
    top: 20px;
    right: 20px;
    width: 150px;
`;

const ErrorText = styled.p`
    color: #ff6b6b;
    font-size: 1rem;
    margin-bottom: 20px;
`;

const HomePage = ({ navigate }) => {
    const [startupLogoError, setStartupLogoError] = useState(false);

    const handleLoginClick = () => {
        navigate('login');
    };

    const handleHabeasdataClick = () => {
        navigate('habeasdata');
    };

    const handleStartupLogoError = () => {
        console.error("No se pudo cargar startupiush.png");
        setStartupLogoError(true);
    };

    return (
        <div>
            <Container>
                <Content>
                    <Button onClick={handleHabeasdataClick}>Registrar emprendedor</Button>
                    <LoginButton onClick={handleLoginClick}>Login</LoginButton>
                </Content>              
            </Container>
        </div>
    );
};

export default HomePage;
