import React from 'react';
import styled from 'styled-components';
import bgImage from './fondo_login.jpg';

const Container = styled.div`
    display: flex;
    height: 100vh;
    height: 100vh; /* Full viewport height */
    width: 100vw; /* Full viewport width */
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(103, 21, 125, 0.4); /* Añadida la transparencia del 20% */
    backdrop-filter: blur(15px);

`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center; /* Alinea al centro horizontalmente */
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%; /* Ancho del contenido */
    margin-left: auto;
`;

const FormContainer = styled.div`
    width: 90%; /* Ajuste el ancho del formulario a un 90% del contenedor */
    max-width: 600px; /* Máximo ancho del formulario para pantallas grandes */
    max-height: 200vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto; /* Habilita el desplazamiento vertical */
    /* Estilos responsivos */
    @media (max-width: 768px) {
        width: 95%;
    }
    @media (max-width: 480px) {
        width: 100%;
        padding: 10px;
    }
`;



const Logo = styled.img`
    width: 200px; /* Ajusta el ancho según sea necesario */
    height: auto; /* Mantén la proporción de la imagen */
    margin-bottom: 2px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center; /* Alinea al centro horizontalmente */
    margin-bottom: 20px;
    font-family: 'Hint Vadodara', sans-serif;
`;

const Button = styled.button`
width: 150px;
height: 50px;
padding: 0px 15px;
display: flex;
justify-content: center;
align-items: center;
border-radius: 50px; /* Cambiado de 23px a 50px */
border: none; /* Eliminado el borde */
background: #213B6B; /* Cambiado el fondo */
box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3); /* Añadido el box-shadow */
color: white; /* Color del texto */
font-family: 'Hint Vadodara', sans-serif;
font-size: 20px; /* Cambiado el tamaño de fuente */
font-weight: 400; /* Cambiado el peso de la fuente */
line-height: normal;
cursor: pointer;
word-wrap: break-word; /* Añadido word-wrap */
margin: 10px; /* Añadido el margen */
`;


const HomePage = ({ navigate }) => {
    const handleLoginClick = () => {
        navigate('login');
    };

    const handleFormEmpClick = () => {
        navigate('registro_emp');
    };

    return (
        <div>
        <Container>
            <BlurOverlay />
        <Content>
        <Logo src="logo.png" alt="Logo" />
            <Description>Formulario de registro para emprendedores</Description>
            <FormContainer>
                <p>sss</p>
            </FormContainer>
        </Content>
    </Container>
    </div>
    );
};


export default HomePage;
