import React from 'react';
import styled from 'styled-components';
import { useState , useEffect } from 'react';
import bgImage from './fondo_admin.jpg'; // Ruta correcta de tu imagen
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const BlurOverlay = styled.div`
    position: absolute;
    top: 0;
    right: 0;
    width: 70%;
    height: 100%;
    background: rgba(33, 59, 107, 0.4); /* azul */
    backdrop-filter: blur(15px);
`;

const Content = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: white;
    padding: 20px;
    position: relative;
    z-index: 1;
    width: 70%;
    margin-left: auto;
`;

const Container = styled.div`
    display: flex;
    height: 100vh;
    width: 100vw;
    position: relative;
    background-image: url(${bgImage});
    background-size: cover;
    background-position: center;
`;
const Logo = styled.img`
    width: 200px;
    height: auto;
    margin-bottom: 5px;
`;

const Description = styled.p`
    font-size: 1.5rem;
    text-align: center;
    margin-bottom: 10px;
    font-family: 'Hint Vadodara', sans-serif;
    color: white;
`;

const FormContainer = styled.div`
    width: 90%;
    max-width: 600px;
    height: 70vh;
    background: rgba(255, 255, 255, 0.9);
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
    overflow-y: auto;
    @media (max-width: 768px) {
        width: 95%;
    }
    @media (max-width: 480px) {
        width: 100%;
        padding: 10px;
    }
`;

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 10px;
`;


const Label = styled.label`
    font-family: 'Hint Vadodara', sans-serif;
    color: black;
    width: 100%;
    max-width: 400px;
    text-align: left;
`;

const Input = styled.input`
    width: 100%;
    max-width: 400px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
`;

const Select = styled.select`
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    width: 100%;
    max-width: 400px;
    position: relative;
`;

const Button = styled.button`
    width: 150px;
    height: 50px;
    padding: 0px 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 50px;
    border: none;
    background: #213B6B;
    box-shadow: 0px 4px 8px rgba(101, 91, 127, 0.3);
    color: white;
    font-family: 'Hint Vadodara', sans-serif;
    font-size: 20px;
    font-weight: 400;
    line-height: normal;
    cursor: pointer;
    word-wrap: break-word;
    margin: 10px;
    transition: background 0.3s;

    &:hover {
        background: #67157D;
    }
`;

const Option = styled.option`
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 20px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #67157D; /* Color de fondo */
    width: 50%;
`;

const DatePickerContainer = styled.div`
    width: 100%;
    max-width: 400px;
    margin-bottom: 10px;
`;

const StyledDatePicker = styled(DatePicker)`
    width: 100%;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-family: 'Hint Vadodara', sans-serif;
    background-color: #f9f9f9;
    color: black;

    &:focus {
        outline: none;
        border-color: #213B6B;
    }
`;

const GlobalStyle = createGlobalStyle`
    .react-datepicker {
        font-family: 'Hint Vadodara', sans-serif;
    }
    .react-datepicker__header {
        background-color: #213B6B;
    }
    .react-datepicker__current-month, .react-datepicker__day-name {
        color: white;
    }
    .react-datepicker__day--selected {
        background-color: #213B6B;
    }
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: center;
    gap: 10px;
`;

// Estilo para el párrafo de descripción del sector
const SectorDescription = styled.p`
    font-family: 'Hint Vadodara', sans-serif;
    color: #213B6B;
    font-size: 0.9rem;
    margin-top: 5px;
    text-align: left;
    width: 100%;
    max-width: 400px;
`;
// Estilo para el mensaje de carga
const LoadingMessage = styled.p`
    font-family: 'Hint Vadodara', sans-serif;
    color: #213B6B;
    font-size: 0.9rem;
`;

const FormPage = ({ navigate }) => {
    const handleInicioEmprednedorClick = () => {
        navigate('inicio_emprendedor');
    };

    const [formData, setFormData] = useState({});
    const [selectedDate, setSelectedDate] = useState(null);
    const [nacionalidades, setNacionalidades] = useState([]);
    const [sectores, setSectores] = useState([]);
    const [loading, setLoading] = useState(true);
    const [descripcionSector, setDescripcionSector] = useState('');
    const [programasAcademicos, setProgramasAcademicos] = useState([]);

    useEffect(() => {
        // Usar la variable nac para las nacionalidades
        let nac = {
            "ok": true,
            "data": [
                {
                    "_id": "6673472cf182f9b41b2c1290",
                    "nombre": "COL",
                    "descripcion": "COLOMBIA"
                },
                {
                    "_id": "66736b2317fb868463e7e60c",
                    "nombre": "ABW",
                    "descripcion": "ARUBA"
                },
                {
                    "_id": "6673713717fb868463e7e615",
                    "nombre": "AND",
                    "descripcion": "ANDORRA"
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        // Usar los datos de 'cosa' para configurar las nacionalidades
        const fetchNacionalidades = async () => {
            try {
                const data = nac;
                setNacionalidades(data.data || []);
            } catch (error) {
                console.error('Error fetching nacionalidades:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchNacionalidades();
    }, []);

    useEffect(() => {
        // Usar la variable 'sector' en lugar de hacer una llamada a fetch
        let sector = {
            "ok": true,
            "data": [
                {
                    "_id": "6670ce4c7acec2e5e71a96d6",
                    "sector": "Tecnología",
                    "descripcion": "Dispositivos electrónicos, desarrollo de software, aplicaciones móviles, servicios de TI, ciberseguridad, y más"
                },
                {
                    "_id": "6670ce887acec2e5e71a96d8",
                    "sector": "Salud y Bienestar",
                    "descripcion": "Clínicas, telemedicina, productos de salud, fitness, y bienestar mental."
                },
                {
                    "_id": "6670ce9f7acec2e5e71a96da",
                    "sector": "Educación",
                    "descripcion": "Plataformas de e-learning, tutorías en línea, capacitación corporativa."
                },
                {
                    "_id": "6670ceb47acec2e5e71a96dc",
                    "sector": "Alimentos y Bebidas",
                    "descripcion": "Restaurantes, alimentos orgánicos, delivery de comida, bebidas artesanales."
                },
                {
                    "_id": "6670cf097acec2e5e71a96de",
                    "sector": "Energía y Medio Ambiente",
                    "descripcion": "Energías renovables, productos sostenibles, reciclaje, gestión de residuos."
                },
                {
                    "_id": "6670cf267acec2e5e71a96e0",
                    "sector": "Fintech",
                    "descripcion": "Servicios financieros digitales, pagos en línea, criptomonedas, asesoría financiera."
                },
                {
                    "_id": "6670cf3e7acec2e5e71a96e2",
                    "sector": "Turismo y Hospitalidad",
                    "descripcion": "Agencias de viajes, alojamiento, experiencias turísticas, guías locales."
                },
                {
                    "_id": "6670cf697acec2e5e71a96e4",
                    "sector": "Construcción e Inmobiliaria",
                    "descripcion": "Desarrollo inmobiliario, servicios de construcción, bienes raíces, decoración de interiores."
                },
                {
                    "_id": "6670cf927acec2e5e71a96e6",
                    "sector": "Logística y Transporte",
                    "descripcion": "Servicios de transporte, logística, almacenamiento, entrega de última milla."
                },
                {
                    "_id": "6670d0007acec2e5e71a96e8",
                    "sector": "Medios y Entretenimiento",
                    "descripcion": "Producción audiovisual, videojuegos, eventos, contenido digital"
                },
                {
                    "_id": "6670d0247acec2e5e71a96ea",
                    "sector": "Agricultura y Agroindustria",
                    "descripcion": "Cultivos, ganadería, tecnología agrícola, productos procesados."
                },
                {
                    "_id": "6670d0577acec2e5e71a96ec",
                    "sector": "Retail y Comercio Electrónico",
                    "descripcion": "Tiendas en línea, marketplaces, dropshipping, venta al por menor."
                },
                {
                    "_id": "6670d0727acec2e5e71a96ee",
                    "sector": "Servicios Profesionales",
                    "descripcion": "Consultoría, marketing, recursos humanos, servicios legales."
                },
                {
                    "_id": "6670d08b7acec2e5e71a96f0",
                    "sector": "Bienes de Consumo",
                    "descripcion": "Productos de consumo masivo, electrodomésticos, artículos para el hogar."
                },
                {
                    "_id": "6670d4067acec2e5e71a96f2",
                    "sector": "Moda y Belleza",
                    "descripcion": "Ropa, accesorios, productos de belleza, cuidado personal, servicios de estilismo."
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        // Usar los datos de 'sector' para configurar los sectores
        const fetchSectores = async () => {
            try {
                const data = sector;
                setSectores(data.data || []);
            } catch (error) {
                console.error('Error fetching sectores:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchSectores();
    }, []);

    useEffect(() => {
        // Simular carga de programas académicos
        let programas = {
            "ok": true,
            "data": [
                {
                    "_id": "prog001",
                    "nombre": "Ingeniería de Sistemas",
                    "descripcion": "Programa de ingeniería enfocado en sistemas computacionales"
                },
                {
                    "_id": "prog002",
                    "nombre": "Administración de Empresas",
                    "descripcion": "Programa enfocado en la gestión y dirección de empresas"
                },
                {
                    "_id": "prog003",
                    "nombre": "Psicología",
                    "descripcion": "Programa enfocado en el estudio de la mente y el comportamiento humano"
                }
            ],
            "message": "Todo ok",
            "error": null
        };

        const fetchProgramasAcademicos = async () => {
            try {
                const data = programas;
                setProgramasAcademicos(data.data || []);
            } catch (error) {
                console.error('Error fetching programas académicos:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchProgramasAcademicos();
    }, []);


    const handleChange = (e) => {
        const { id, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [id]: value,
        }));

        // Actualizar la descripción del sector basado en el sector seleccionado
        if (id === 'sectorIndustria') {
            const selectedSector = sectores.find(sector => sector.sector === value);
            setDescripcionSector(selectedSector ? selectedSector.descripcion : '');
        }
    };

    const handleDateChange = (date) => {
        setSelectedDate(date);
        setFormData((prevData) => ({
            ...prevData,
            fecha: date,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const formattedData = {
            nombreEmprendimiento: formData.nombreEmprendedor || '',
            idSector: formData.sectorIndustria || '',
            idEmprendedor: formData.documentoIdentidad || '',
            fecha: formData.fecha ? formData.fecha.toISOString().split('T')[0] : '',
            respuestas: [
                { idPregunta: '6679e2892c652ff30f7ce53f', respuestaPregunta: formData.ideaClara === 'si' },
                { idPregunta: '6679e2a02c652ff30f7ce541', respuestaPregunta: formData.investigacionesMercado === 'si' },
                { idPregunta: '6679e2af2c652ff30f7ce543', respuestaPregunta: formData.necesidadProblema === 'si' },
                { idPregunta: '6679e2d12c652ff30f7ce545', respuestaPregunta: formData.retroalimentacionClientes === 'si' },
                { idPregunta: '6679e2e52c652ff30f7ce547', respuestaPregunta: formData.propuestaValor === 'si' },
                { idPregunta: '6679e2f92c652ff30f7ce549', respuestaPregunta: formData.planNegocios === 'si' },
                { idPregunta: '6679e30b2c652ff30f7ce54b', respuestaPregunta: formData.productosServicios === 'si' },
                { idPregunta: '6679e31f2c652ff30f7ce54d', respuestaPregunta: formData.prototipoPrueba === 'si' },
                { idPregunta: '6679e3352c652ff30f7ce54f', respuestaPregunta: formData.competenciaAnalisis === 'si' },
                { idPregunta: '6679e34a2c652ff30f7ce551', respuestaPregunta: formData.publicoObjetivo === 'si' },
                { idPregunta: '6679e3622c652ff30f7ce553', respuestaPregunta: formData.planFinanciero === 'si' },
                { idPregunta: '6679e3782c652ff30f7ce555', respuestaPregunta: formData.enElMercado === 'si' },
                { idPregunta: '6679e38e2c652ff30f7ce557', respuestaPregunta: formData.campañaMarketing === 'si' },
                { idPregunta: '6679e3a42c652ff30f7ce559', respuestaPregunta: formData.clientesUsuarios === 'si' },
                { idPregunta: '6679e3ba2c652ff30f7ce55b', respuestaPregunta: formData.canalDistribucion === 'si' },
                { idPregunta: '6679e3d02c652ff30f7ce55d', respuestaPregunta: formData.presenciaEnLinea === 'si' },
                { idPregunta: '6679e3e62c652ff30f7ce55f', respuestaPregunta: formData.estrategiasPrecios === 'si' },
                { idPregunta: '6679e3fc2c652ff30f7ce561', respuestaPregunta: formData.ingresosConstantes === 'si' },
                { idPregunta: '6679e4122c652ff30f7ce563', respuestaPregunta: formData.reinversionCrecimiento === 'si' },
                { idPregunta: '6679e4282c652ff30f7ce565', respuestaPregunta: formData.contratandoEmpleados === 'si' },
                { idPregunta: '6679e43e2c652ff30f7ce567', respuestaPregunta: formData.ampliadoGama === 'si' },
                { idPregunta: '6679e4542c652ff30f7ce569', respuestaPregunta: formData.metricasKPI === 'si' },
                { idPregunta: '6679e46a2c652ff30f7ce56b', respuestaPregunta: formData.sistemasGestion === 'si' },
                { idPregunta: '6679e4802c652ff30f7ce56d', respuestaPregunta: formData.baseClientes === 'si' },
                { idPregunta: '6679e4962c652ff30f7ce56f', respuestaPregunta: formData.puntoEquilibrio === 'si' },
